-- Quest: canon-quest-spb-2061-2077-imperial-crown
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2061-2077-imperial-crown', 'Санкт-Петербург 2061-2077 — «Императорская корона»', 'Игрок выполняет заказ петербургских патриотов, крадёт корону из Кремля и решает дальнейшую судьбу символа власти.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2061-2077-imperial-crown", "title": "Санкт-Петербург 2061-2077 — «Императорская корона»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "heist", "politics"], "topics": ["city_quests", "political_conflict"], "related_systems": ["narrative-service", "combat-service", "stealth-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2061-2077/quest-039-imperial-crown.md", "visibility": "internal", "audience": ["concept", "narrative", "gameplay"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сюжет ограбления Алмазного фонда представлен без системной разбивки и не учитывает последствия для Москвы и Петербурга.", "goal": "Описать квест-хайст по похищению императорской короны с учётом ветвлений, политических исходов и наград.", "essence": "Игрок выполняет заказ петербургских патриотов, крадёт корону из Кремля и решает дальнейшую судьбу символа власти.", "key_points": ["Экстремальный хайст с использованием стелса, взлома и боевых сценариев.", "Три возможных исхода, влияющих на политическую карту и репутации фракций.", "Влияние на межгородское противостояние Москва vs Санкт-Петербург."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Ключевой сюжет линии патриотов Петербурга. Игрок отправляется в Москву, чтобы похитить Большую Императорскую корону\\nиз Алмазного фонда и привезти её на Неву или распорядиться иначе.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Контракт от петербургских фракций и подготовка к операции.\\n2. Инфильтрация Московского Кремля и сбор данных о системах безопасности.\\n3. Проникновение в Алмазный фонд с использованием стелса и взлома.\\n4. Извлечение короны и противостояние охране.\\n5. Эвакуация и выбор дальнейшей стратегии.\\n", "mechanics_links": ["mechanics/combat/combat-pvp-pve.yaml"], "assets": []}, {"id": "choices", "title": "Выборы", "body": "- Вернуть корону в Петербург: усиливает патриотическую линию, провоцирует конфликт с Москвой.\\n- Вернуть Москве: предательство заказчиков, но стабилизация отношений между городами.\\n- Продать на чёрном рынке: огромная прибыль, но статус врага обоих мегаполисов.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Красный бархат витрин Алмазного фонда и лазерные решётки безопасности.\\n- Гигантский рубин и 4 936 бриллиантов короны как визуальные акценты.\\n- Соперничество Москвы и Петербурга, подчёркнутое репликами NPC.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- XP: 15 000, Валюта: до 1 000 000 000 (при продаже).\\n- Политические модификаторы, влияющие на доступ к городским сюжетам.\\n- Запуск цепочек «война городов», «политический компромисс» или «изгнанник».\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Хайст с комбинированием стелса и динамического боя.\\n- Политическая система, изменяющая состояние мегаполисов в зависимости от решения.\\n- Репутационные последствия у корпораций, патриотов и криминальных сетей.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с московской линейкой квестов и глобальными сюжетами о наследии империи.\\nИсход определяет доступ к событиям межгородского противостояния и влияет на экономические показатели регионов.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Императорская корона» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

