-- Quest: canon-quest-spb-2061-2077-smolny-institute
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2061-2077-smolny-institute', 'Санкт-Петербург 2061-2077 — «Смольный Институт»', 'Игрок сдаёт экзамены, проходит ускорённое обучение и выпускается с титулом «Институтка», усиливая статус Санкт-Петербурга как культурного центра.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2061-2077-smolny-institute", "title": "Санкт-Петербург 2061-2077 — «Смольный Институт»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "education"], "topics": ["imperial-traditions", "elite-schools"], "related_systems": ["narrative-service", "social-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2061-2077/quest-040-smolny-institute.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о возвращении Смольного института оставался в Markdown и не был связан с образовательной линией города.", "goal": "Структурировать сюжет о восстановленном Смольном, где игрок проходит имперское обучение и получает культурные бонусы.", "essence": "Игрок сдаёт экзамены, проходит ускорённое обучение и выпускается с титулом «Институтка», усиливая статус Санкт-Петербурга как культурного центра.", "key_points": ["Иммерсивное обучение в стиле исторического института благородных девиц.", "Мини-игры по этикету, искусствам и языкам привязаны к социальным механикам.", "Выпускной бал и титул дают долгосрочные социальные бонусы."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "В 2070-х Смольный институт открывается как элитная школа старого образца. Игрок проходит вступительные испытания,\\nпогружается в имперские традиции и получает редкий титул, повышающий культурную репутацию и доступ к закрытым\\nсалонам.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Вступительные экзамены и собеседование с кураторами.\\n2. Обучение по четырём блокам: этикет, языки, искусство, наука.\\n3. Симуляция учебных лет через мини-игры и социальные сцены.\\n4. Выпускной бал с приглашениями фракций и выбором партнёра.\\n5. Получение диплома и титула «Институтка».\\n", "mechanics_links": ["mechanics/social/relationships-system.yaml"], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Смольный собор служит главной площадкой церемоний, а восстановленные классы оформлены в голубых оттенках\\nбарокко. Форма воспитанниц — белые платья с имперскими лентами. Строгие правила поведения, расписание и речи\\nнаставников отсылают к эпохе Екатерины II.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 5 000 XP и очки атрибутов: Образование +20, Культура +15, Этикет +10.\\n- Титул «Институтка», открывающий доступ к дипломатическим приёмам.\\n- Репутационный бонус с имперскими клубами и будущие сюжетные ветки.\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Образовательные мини-игры с оценкой знаний.\\n- Социальные сцены элитного общества с проверкой этикета.\\n- Долгосрочный бафф к культурному влиянию города.\\n", "mechanics_links": ["mechanics/social/mentorship-system.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест поддерживает императорскую линию Санкт-Петербурга и подготавливает игрока к событиям культурной лиги и\\nдипломатическим миссиям региона.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Смольный Институт» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

