INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-almaty-2020-2029-apple-capital', 'Алматы 2020-2029 — «Столица яблок»', 'Игрок посещает саламаты, дегустирует Апорт, участвует в фестивале и покупает местные продукты.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-almaty-2020-2029-apple-capital", "title": "Алматы 2020-2029 — «Столица яблок»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T01:30:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["almaty", "quest", "apples"], "topics": ["culinary-experience", "cultural-heritage"], "related_systems": ["narrative-service", "economy-service", "wellbeing-service"], "related_documents": [{"id": "github-issue-923", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/923", "relation": "migrated_to", "migrated_at": "2025-11-23T01:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/almaty/2020-2029/quest-003-apple-capital.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Не хватало кулинарного квеста, раскрывающего яблочный бренд Алматы.", "goal": "Показать исторический и гастрономический маршрут, посвящённый сортам Апорт и яблочным традициям.", "essence": "Игрок посещает саламаты, дегустирует Апорт, участвует в фестивале и покупает местные продукты.", "key_points": ["Алматы — родина диких яблонь и сорта Апорт.", "Квест сочетает дегустацию, историю и торговлю.", "Бафф здоровья