INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2030-2039-pavlovsk-palace', 'Санкт-Петербург 2030-2039 — «Павловский дворец»', 'Игрок раскрывает трагическую историю Павла I через экскурсию, VR-сцены и встречу с ИИ-призраком правителя.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2030-2039-pavlovsk-palace", "title": "Санкт-Петербург 2030-2039 — «Павловский дворец»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "palace"], "topics": ["city_quests", "historical-mystery"], "related_systems": ["narrative-service", "liveops-service"], "related_documents": [{"id": "github-issue-527", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/527", "relation": "migrated_to", "migrated_at": "2025-11-22T22:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2030-2039/quest-017-pavlovsk-palace.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Дворцовый квест по Павловску не был структурирован в базе знаний и не включал VR-реконструкцию.", "goal": "Зафиксировать сценарий исследования дворца и парка с мистической линией Павла I.", "essence": "Игрок раскрывает трагическую историю Павла I через экскурсию, VR-сцены и встречу с ИИ-призраком правителя.", "key_points": ["Визит в дворец и парк Павловска с культурными подробностями.", "VR-реконструкция последних дней императора.", "Мистический контакт с ИИ-симуляцией «призрака» Павла I."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Игрок отправляется в Павловск, погружается в историю правителя и сталкивается с ИИ, воссоздающим его личность.\\nКвест сочетает экскурсию, VR-сцены и событийные решения.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Добраться до Павловска и встретиться с гидом.\\n2. Изучить парадные залы дворца и экспозиции.\\n3. Пройти VR-реконструкцию последних дней Павла I.\\n4. Исследовать парк и скрытые павильоны.\\n5. Встретить ИИ-симуляцию «призрака» Павла и решить, что делать с данными.\\n", "mechanics_links": ["mechanics/world/events/live-events-system.yaml"], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Жёлтый фасад и строгие залы классицизма.\\n- Огромный парк с павильонами и реками.\\n- Архивы, рассказывающие о заговоре против Павла I.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- XP 2 000, культура +10, эмоции от мистической встречи.\\n- Возможность получить доступ к закрытым архивам дворцовой линии.\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Экскурсионный и мистический контент.\\n- VR-сцены с интерактивными выборами.\\n- Интерпретация «призрака» как ИИ, задающего вопросы игроку.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест развивает дворцовую линию пригородов Петербурга и может влиять на квесты «Янтарная комната» и «Культурная столица».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 527, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Павловский дворец» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

