-- Quest: canon-quest-spb-2030-2039-ballet-premiere
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2030-2039-ballet-premiere', 'Санкт-Петербург 2030-2039 — «Премьера балета»', 'Игрок проходит путь от прослушивания до триумфа на сцене, укрепляя связи с культурной элитой Петербурга.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2030-2039-ballet-premiere", "title": "Санкт-Петербург 2030-2039 — «Премьера балета»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "ballet"], "topics": ["city_quests", "career"], "related_systems": ["narrative-service", "progression-service", "social-service"], "related_documents": [{"id": "github-issue-527", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/527", "relation": "migrated_to", "migrated_at": "2025-11-22T22:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2030-2039/quest-018-ballet-premiere.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Карьерный путь в Мариинском театре описан только в Markdown и не интегрирован в систему культурных карьер.", "goal": "Создать структурированный квест о восхождении солиста балета с тренировками, репетициями и премьерой.", "essence": "Игрок проходит путь от прослушивания до триумфа на сцене, укрепляя связи с культурной элитой Петербурга.", "key_points": ["Карьерный прогресс через интенсивные тренировки и выборы ролей.", "Репетиции с легендарными хореографами и живой оркестр.", "Премьера на сцене Мариинки и реакция зрителей."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест рассказывает о карьере в Мариинском театре: от первого прослушивания до титула примы. Игроку предстоит\\nвыдержать конкуренцию, показать мастерство и не подвести партнёров на премьере.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Успешно пройти прослушивание и получить приглашение в труппу.\\n2. Пройти ускоренный цикл тренировок и мини-игр на выносливость.\\n3. Выбрать репертуар и главную партию, доказав готовность.\\n4. Провести репетиции с хореографом и оркестром.\\n5. Выступить на премьере и получить оценку критиков.\\n", "mechanics_links": ["mechanics/world/events/live-events-system.yaml"], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Сцена Мариинского театра с синей и золотой отделкой.\\n- Классические костюмы и пуанты, оцифрованные в AR.\\n- Музыка Чайковского и овации публики.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- XP 5 000, ежемесячный доход 20 000 eddies, фейм +20.\\n- Титул «Прима Мариинки».\\n- Новые социальные контакты в высшем обществе и доступ к элитным мероприятиям.\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Мини-игры танца и тренировки выносливости.\\n- Управление карьерой и отношениями с труппой.\\n- Система оценки зрителей и критиков.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связан с культурной линией Петербурга, дополняет события Мариинского театра и влияет на сезонные концерты\\nи коллаборации с мировыми площадками.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 527, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Премьера балета» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

