-- Quest: canon-quest-spb-2078-2093-peter-legacy
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2078-2093-peter-legacy', 'Санкт-Петербург 2078-2093 — «Наследие Петра»', 'Игроки вступают в гильдию, выполняют строительные и реформаторские задания и соревнуются за лидерство в рейтинге Legacy.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2078-2093-peter-legacy", "title": "Санкт-Петербург 2078-2093 — «Наследие Петра»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "guild"], "topics": ["legacy-system", "city-building"], "related_systems": ["narrative-service", "guild-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2078-2093/quest-042-peter-legacy.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Гильдейская линия «Птенцы Гнезда Петрова» существовала только в Markdown и не была формализована для Legacy-системы.", "goal": "Описать гильдейский контент, где команды восстанавливают город в духе Петра I и зарабатывают очки наследия.", "essence": "Игроки вступают в гильдию, выполняют строительные и реформаторские задания и соревнуются за лидерство в рейтинге Legacy.", "key_points": ["Многопрофильные миссии: строительство, флот, реформы, наука.", "Очки Legacy дают гильдейские бонусы и влияние на культурную карту города.", "Тематические награды с символикой Петра поддерживают имперскую линию."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "«Наследие Петра» вводит игроков в гильдию «Птенцы Петра». Участники должны доказать преданность реформаторскому\\nдуху, помогая модернизировать Петербург, восстанавливать дворцы и запускать научные инициативы.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "missions", "title": "Миссии гильдии", "body": "- Строительство: восстановление дворцов и верфей по историческим чертежам.\\n- Реформы: внедрение городских сервисов и модернизация инфраструктуры.\\n- Флот: морские экспедиции и защита торговых путей.\\n- Наука: запуск исследовательских лабораторий и образовательных программ.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "legacy_system", "title": "Legacy-очки", "body": "Legacy-очки начисляются за вклад в каждое направление. Еженедельные рейтинги определяют лидеров, открывая доступ\\nк уникальным контрактам и городским привилегиям. Девиз гильдии «Делай как Пётр» отражён в визуальном стиле и\\nцеремониях награждения.\\n", "mechanics_links": ["mechanics/social/mentorship-relationships.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 5 000 XP и гильдейские бонусы, усиливающие строительство и социальное влияние.\\n- Титул «Птенец Гнезда Петрова» и доступ к имперскому триколору и Андреевскому флагу.\\n- Специальные проекты города, зависящие от суммарных Legacy-очков.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Коллективная игра гильдий с рейтинговой таблицей.\\n- Мультидисциплинарные задания в строительстве, флоте и науке.\\n- Постоянный вклад в развитие города и имперской линии.\\n", "mechanics_links": ["mechanics/social/npc-hiring-system.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связан с событиями культурной лиги, поддерживает строительные кампании региона и формирует базу для\\nдальнейших сюжетов про реформы Санкт-Петербурга.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация гильдейского квеста «Наследие Петра» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

