INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-almaty-2020-2029-shymbulak-ski', 'Алматы 2020-2029 — «Лыжи на Шымбулаке»', 'Игрок поднимается по канатке, выбирает трассу и проходит мини-игру слалома, завершая день в горном кафе.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-almaty-2020-2029-shymbulak-ski", "title": "Алматы 2020-2029 — «Лыжи на Шымбулаке»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["almaty", "quest", "skiing"], "topics": ["mountain-sports", "tourism"], "related_systems": ["narrative-service", "activity-service", "travel-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/almaty/2020-2029/quest-005-shymbulak-ski.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Спортивная линия Алматы требовала подробного описания горнолыжного опыта на Шымбулаке.", "goal": "Добавить высокогорный спуск с выбором трассы, ощущением высоты и apres-ski сценой.", "essence": "Игрок поднимается по канатке, выбирает трассу и проходит мини-игру слалома, завершая день в горном кафе.", "key_points": ["Высота 3 200 м влияет на стамину и зрение.", "Система трасс поддерживает разный уровень навыка.", "Apres-ski закрепляет атмосферу курорта."]}, "content": {"sections": [{"id": "ascent", "title": "Подъём", "body": "Канатная дорога от Медео переносит игроков из города в горы за 20 минут. На промежуточных станциях доступны магазины и ремонт\\nэкипировки.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "summit", "title": "Вершина", "body": "На высоте 3 200 метров игрок ощущает разреженный воздух. Появляется выбор трассы: зелёная для новичков, синяя для среднего и\\nчёрная для экспертов.\\n", "mechanics_links": ["mechanics/activities/mountain-ski.yaml"], "assets": []}, {"id": "run", "title": "Спуск", "body": "Мини-игра слалома измеряет точность, скорость и трюки. Погодные эффекты и панорама Алматы усиливают визуальный опыт.\\n", "mechanics_links": ["mechanics/minigames/slalom.yaml"], "assets": []}, {"id": "apres", "title": "Apres-ski", "body": "В горном кафе игрок встречает NPC-спортсменов, обсуждает будущие соревнования и получает бафф к спортивным активностям.\\n", "mechanics_links": ["mechanics/social/mountain-lounge.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 2 000 XP, спортивный модификатор +15, затраты 100 еддис.\\n- Ачивка «Горнолыжник Тянь-Шаня» и доступ к олимпийской тренировке.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связан с зимними турнирами Алматы, маршрутами Медео и международными спортивными событиями региона.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 925, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Лыжи Шымбулак» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED