INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-almaty-2020-2029-green-bazaar', 'Алматы 2020-2029 — «Зелёный базар»', 'Игрок погружается в шумный рынок, торгуется за фрукты и уносит запас восточных лакомств.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-almaty-2020-2029-green-bazaar", "title": "Алматы 2020-2029 — «Зелёный базар»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["almaty", "quest", "market"], "topics": ["commerce", "cultural-experience"], "related_systems": ["narrative-service", "commerce-service", "food-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/almaty/2020-2029/quest-006-green-bazaar.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Торговая линия Алматы нуждалась в живом рынке с элементами торга и гастрономии.", "goal": "Добавить сценарий погружения в Көк базар с мини-игрой торга и дегустацией национальных продуктов.", "essence": "Игрок погружается в шумный рынок, торгуется за фрукты и уносит запас восточных лакомств.", "key_points": ["Торг как ключевой механизм экономии и репутации.", "Ассортимент подчёркивает казахскую кухню и сезонные продукты.", "Атмосфера восточного базара усиливается звуками и ароматами."]}, "content": {"sections": [{"id": "arrival", "title": "Прибытие на базар", "body": "Толпы покупателей, выкрики продавцов и витрины специй встречают игрока. Требуется подобрать подходящий стиль одежды для\\nскидок.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "bargaining", "title": "Искусство торга", "body": "Мини-игра торга использует реплики, жесты и знание цен. Успех снижает стоимость и повышает репутацию среди торговцев.\\n", "mechanics_links": ["mechanics/commerce/bargaining.yaml"], "assets": []}, {"id": "tasting", "title": "Дегустация", "body": "Игрок пробует кумыс, апорт и баурсаки. Каждая дегустация даёт временные баффы и открывает лор о происхождении продукта.\\n", "mechanics_links": ["mechanics/food/tasting-event.yaml"], "assets": []}, {"id": "haul", "title": "Покупки и трофеи", "body": "Сформирован набор товаров: фрукты, курт, специи. Вес влияет на выносливость, поэтому важно выбрать, что взять с собой.\\n", "mechanics_links": ["mechanics/inventory/weight-management.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 600 XP, набор еды, траты 50 еддис, ачивка «Базарный король».\\n- Бонус к торговым операциям в регионе.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест соединяет гастрономические фестивали Алматы, контрабандные ветки рынка и торговые линии Центральной Азии.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 926, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Зелёный базар» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.require