INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-spb-2093-neva-ice', 'Санкт-Петербург 2078-2093 — Лёд Невы', 'Игрок ждёт сильных морозов, пересекает Неву по льду и получает бафф закалки.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-spb-2093-neva-ice", "title": "Санкт-Петербург 2078-2093 — Лёд Невы", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "winter", "tradition"], "topics": ["survival", "cultural_event"], "related_systems": ["narrative-service", "survival-service"], "related_documents": [{"id": "quest-spb-2040-ice-rafting", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2078-2093/quest-045-neva-ice.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Традиция зимнего перехода по замёрзшей Неве оставалась в Markdown и не формализовывала риски и награды.", "goal": "Описать сезонное событие «Лёд Невы» с системой проверки на стойкость и опциональным купанием.", "essence": "Игрок ждёт сильных морозов, пересекает Неву по льду и получает бафф закалки.", "key_points": ["Требуется ожидание устойчивых морозов и проверка смелости.", "Событие усиливает зимнюю идентичность города.", "Участники получают ачивку «Ледяное сердце Невы» и бонус к сопротивляемости холоду."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "«Лёд Невы» — зимний ритуал, когда жители и гости города переходят реку по толстому льду. Событие сочетает риск,\\nтрадицию и эмоциональную связь с Петербургом.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Дождаться морозов и убедиться, что толщина льда безопасна.\\n2. Стартовать на западном берегу и двигаться к противоположной стороне.\\n3. Проходить по обозначенным маршрутам, преодолевая трещины и скользкие участки.\\n4. Опционально окунуться в прорубь в рамках крещенской традиции.\\n5. Вернуться на берег и завершить испытание.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Мороз -20 °C, туман от дыхания, предупреждения МЧС и местные поговорки «Не ходите по льду!». На берегах — пункты\\nобогрева и горячий чай.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 500 XP.\\n- Бафф «Закалка» +15 к сопротивлению холоду.\\n- Ачивка «Ледяное сердце Невы».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Проверки стойкости и ловкости, риск провала под лёд, зимние погодные эффекты и социальные реакции прохожих.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Поддерживает зимнюю линию Санкт-Петербурга и подготавливает участников к финальному событию «Вечный город».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Лёд Невы» в формат знаний Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

