-- Quest: canon-quest-spb-2040-2060-canal-races
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2040-2060-canal-races', 'Санкт-Петербург 2040-2060 — «Гонки по каналам»', 'Игроки вступают в экстремальные гонки между мостами, лавируя под низкими пролётами и соревнуясь за легендарный гидроцикл.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2040-2060-canal-races", "title": "Санкт-Петербург 2040-2060 — «Гонки по каналам»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "racing"], "topics": ["city_quests", "competitive-events"], "related_systems": ["narrative-service", "liveops-service"], "related_documents": [{"id": "github-issue-528", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/528", "relation": "migrated_to", "migrated_at": "2025-11-22T22:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2040-2060/quest-022-canal-races.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Гонки на гидроциклах по каналам описаны без структурированной логики события и не интегрированы в liveops-план.", "goal": "Оформить водный турнир с регистрацией, модификациями техники и маршрутами по ключевым каналам Санкт-Петербурга.", "essence": "Игроки вступают в экстремальные гонки между мостами, лавируя под низкими пролётами и соревнуясь за легендарный гидроцикл.", "key_points": ["Регистрация с денежным взносом и подготовкой техники.", "Маршрут через Неву, Мойку, Грибоедова, Фонтанку и Крюков канал.", "Препятствия: мосты, резкие повороты, трафик гондол."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест представляет собой еженедельное событие liveops. Участник оплачивает взнос, тюнингует гидроцикл и выходит\\nна трассу по каналам Петербурга, соревнуясь с местными профессионалами и туристами-драйверами.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Зарегистрироваться и оплатить взнос 2 000 eddies.\\n2. Настроить гидроцикл: усилить устойчивость, установить турбонаддув.\\n3. Пройти квалификационный заезд по Неве.\\n4. Основная гонка: Мойка → Грибоедова → Фонтанка → Крюков канал.\\n5. Финиш у Адмиралтейства и награждение, включая легендарный гидроцикл.\\n", "mechanics_links": ["mechanics/world/events/live-events-system.yaml"], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Низкие мосты, требующие наклоняться или снижать скорость.\\n- Фасады дворцов и отражения на воде.\\n- Зрители на набережных и мостах.\\n- Гондолы и прогулочные лодки как динамические препятствия.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 1 место: 25 000 eddies и уникальный гидроцикл «Канальный гонщик».\\n- Участие: XP 1 500 и прогресс в ранговой таблице.\\n- Репутация в водной лиге и доступ к элитным гонкам.\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Аркадные водные гонки с управлением на высоких скоростях.\\n- Система штрафов за столкновения с мостами и судоходством.\\n- Онлайн-таблица лидеров и сезонные награды.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Событие поддерживает водную liveops-линию Петербурга, связывается с пиратскими и туристическими сюжетами и\\nможет комбинироваться с фестивалями на набережных.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 528, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Гонки по каналам» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

