INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-spb-2093-palace-concert', 'Санкт-Петербург 2078-2093 — Дворцовый концерт', 'Игрок выбирает один из имперских дворцов, слушает синхронный концерт и прощается с сезоном.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-spb-2093-palace-concert", "title": "Санкт-Петербург 2078-2093 — Дворцовый концерт", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "classical-music", "finale"], "topics": ["cultural_event", "live_concert"], "related_systems": ["narrative-service", "liveops-service"], "related_documents": [{"id": "quest-spb-2030-imperial-ball", "relation": "echoes"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2078-2093/quest-047-palace-concert.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Финальный дворцовый концерт оставался в Markdown и не раскрывал синхронную структуру события.", "goal": "Задокументировать мульти-локационный концерт классики с финалом «Лебединое озеро».", "essence": "Игрок выбирает один из имперских дворцов, слушает синхронный концерт и прощается с сезоном.", "key_points": ["Пять дворцов выступают одновременно с одной программой.", "Оркестры в исторических костюмах создают атмосферу имперской роскоши.", "Финал сопровождается эмоциями и формирует ачивку «Свидетель симфонии»."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "«Дворцовый концерт» превращает Зимний дворец, Екатерининский дворец, Петергоф, Павловск и Михайловский дворец в\\nсинхронные концертные залы. Классическая программа подчеркивает культурное наследие города.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Выбрать один из дворцов и занять место в зале.\\n2. Прослушать симфонический блок с произведениями Чайковского, Мусоргского и Римского-Корсакова.\\n3. Наблюдать синхронизацию оркестров через holo-связь между дворцами.\\n4. Финальная часть — «Лебединое озеро» под проекции белых лебедей.\\n5. Участвовать в аплодисментах и получить культурный бафф.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Золотые залы, мерцающие люстры, акустика имперских залов и hologram-связь между площадками создают эффект единой симфонии.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 3 000 XP.\\n- Финансы: -200 еддис (билеты и наряды).\\n- Культурный бафф +20 и ачивка «Свидетель симфонии».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Синхронное управление несколькими сценами, культурные баффы и социальные реакции зрителей.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Завершает культурную арку Петербурга и ведёт к финалу «Вечный город».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Дворцовый концерт» в формат знаний Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

