-- Quest: quest-010-great-chicago-fire
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-010-great-chicago-fire', 'Чикаго — Великий пожар 1871', 'Игрок посещает музей, исследует хронику пожара и возвращения города, получая исторические баффы и доступ к архитектурным веткам.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-010-great-chicago-fire", "title": "Чикаго — Великий пожар 1871", "document_type": "canon", "category": "lore", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-09T11:23:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["chicago", "history", "disaster"], "topics": ["timeline_author", "regional_quests"], "related_systems": ["quest-engine", "heritage-service", "education-service"], "related_documents": [{"id": "questline-chicago-history", "relation": "collection"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/chicago/2020-2029/quest-010-great-chicago-fire.md", "visibility": "internal", "audience": ["concept", "narrative", "education"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Исторический квест о пожаре 1871 года требует структурирования и связи с архитектурным возрождением города.", "goal": "Показать путь Чикаго от катастрофы к изобретению небоскрёба и образовательным инициативам.", "essence": "Игрок посещает музей, исследует хронику пожара и возвращения города, получая исторические баффы и доступ к архитектурным веткам.", "key_points": ["Восстановление 90% города и переход к стальному каркасу.", "Легенда о корове миссис О''Лири как культурный миф.", "Связь пожара с последующими архитектурными нововведениями."]}, "content": {"sections": [{"id": "introduction", "title": "Введение", "body": "Великий чикагский пожар 1871 года уничтожил большую часть города, но стал отправной точкой для его возрождения. Квест фиксирует хронологию, человеческие истории и последующее появление небоскрёбов.\\n", "mechanics_links": ["mechanics/social/event-system.yaml"], "assets": []}, {"id": "regional_context", "title": "Исторические факты", "body": "- Пожар длился три дня, уничтожив 17 500 зданий и оставив без крова более 100 000 жителей.\\n- Легенда о корове миссис О''Лири остаётся популярным, но спорным объяснением.\\n- На место деревянного города пришли каменные и стальные конструкции, рождённые Chicago School.\\n", "mechanics_links": ["canon/lore/_03-lore/timeline-author/quests/america/chicago/chicago-history.yaml"], "assets": []}, {"id": "quest_flow", "title": "Этапы квеста", "body": "1. Посетить Chicago History Museum и пройти интерактивную экспозицию пожара.\\n2. Исследовать хронику событий: начало 8 октября, три дня горения, последствия.\\n3. Узнать о восстановлении, появлении стального каркаса и новых архитекторов.\\n4. Получить доступ к маршрутам «Phoenix Trail» с обзором современного города.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 3 000 XP, показатель «История» +25.\\n- Ачивка «Феникс Чикаго».\\n- Разблокировка образовательных мероприятий и архитектурных цепочек.\\n", "mechanics_links": ["mechanics/progression/rewards-catalog.yaml"], "assets": []}, {"id": "immersion", "title": "Атмосфера и связи", "body": "Квест служит основой для образовательных программ, связывает историческую линию с архитектурной и позволяет командам событий запускать годовщины и реконструкции.\\n", "mechanics_links": ["canon/lore/_03-lore/timeline-author/quests/america/chicago/2020-2029/index.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/vision/review.yaml#quest-chicago-2029-010"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-09", "author": "concept_director", "changes": "Формализован исторический квест о Великом чикагском пожаре и его последствиях."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

