INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-spb-2077-russian-museum-night', 'Санкт-Петербург 2061-2077 — Ночь в Русском музее', 'Персонаж остаётся после закрытия, общается с цифровыми проекциями мастеров и путешествует внутри картин.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-spb-2077-russian-museum-night", "title": "Санкт-Петербург 2061-2077 — Ночь в Русском музее", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "museum", "art"], "topics": ["culture", "immersive-storytelling"], "related_systems": ["narrative-service", "vr-service"], "related_documents": [{"id": "quest-spb-cultural-capital", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2061-2077/quest-033-russian-museum-night.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Линии музеев Петербурга требовался интерактивный опыт с оживающими картинами.", "goal": "Дать игроку ночь в Русском музее с погружением в классические полотна и беседами с ИИ-художниками.", "essence": "Персонаж остаётся после закрытия, общается с цифровыми проекциями мастеров и путешествует внутри картин.", "key_points": ["Русский музей открывает VR- и AR-доступ к знаковым полотнам.", "ИИ-версии Репина, Айвазовского, Врубеля и Кандинского ведут диалоги.", "Награда повышает культурный атрибут и раскрывает музейную линию."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Игрок становится гостем закрытой «Ночи в Русском музее», где картины оживают и погружают в эпоху великих русских\\nхудожников посредством AR/VR-симуляций.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Оформить договор и остаться в музеe после закрытия.\\n2. Наблюдать, как экспонаты оживают с помощью смешанной реальности.\\n3. Общаться с ИИ-версией Репина, Айвазовского, Врубеля и Кандинского.\\n4. Войти внутрь выбранных картин «Бурлаки на Волге», морских пейзажей, «Демона» и абстрактных полотен.\\n5. Вернуться к утру, получив культурный отчёт и новые диалоги в музейной сети.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Михайловский дворец, парадные залы, золотые рамы и исторические костюмы ИИ-гида подчёркивают атмосферу легендарного\\nмузея русского искусства.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 3 000 XP.\\n- Атрибут «Культура» +20.\\n- Ачивка «Друг художников».\\n- Доступ к эксклюзивным музейным мероприятиям.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- VR-погружение в полотна.\\n- ИИ-диалоги, раскрывающие историю создания картин.\\n- Образовательные элементы и коллекция воспоминаний.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Расширяет музейную линию Петербурга и служит связующим квестом к культурным событиям города.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Ночь в Русском музее» в формат знаний Concept Director."}], "validation": {"checksum": ""