-- Quest: canon-quest-spb-2078-2093-goodbye-petersburg
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2078-2093-goodbye-petersburg', 'Санкт-Петербург 2078-2093 — «Прощай, Петербург»', 'Игрок проводит день в культовых местах, фиксирует эмоции и готовится к финальному переходу в новую лигу.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2078-2093-goodbye-petersburg", "title": "Санкт-Петербург 2078-2093 — «Прощай, Петербург»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "farewell", "tour"], "topics": ["cultural-tour", "emotional-ending"], "related_systems": ["narrative-service", "social-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2078-2093/quest-049-goodbye-petersburg.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Прощальный тур по Петербургу оставался в Markdown и не был встроен в эмоциональную арку финального сезона.", "goal": "Структурировать квест последнего дня в городе, подчёркивающий символические локации, эмоции и памятные награды.", "essence": "Игрок проводит день в культовых местах, фиксирует эмоции и готовится к финальному переходу в новую лигу.", "key_points": ["Маршрут проходит через пять временных точек суток: рассвет, утро, день, закат и ночь.", "Эмоциональные выборы формируют персональный журнал воспоминаний и влияют на отношение фракций.", "Награда включает ачивку «Петербургская душа» и бафф прощания перед квестом «Вечный Город»."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "«Прощай, Петербург» рассчитан на один игровой день. Игрок посещает символические локации, чтобы завершить личную\\nисторию в городе и зафиксировать эмоции перед отъездом.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "itinerary", "title": "Маршрут", "body": "1. Рассвет на Дворцовой площади и воспоминания о старте сезона.\\n2. Утро на Невском проспекте с уличными музыкантами и случайными NPC.\\n3. Дневная гондола по каналам с фоточекпоинтами.\\n4. Закат у Стрелки Васильевского острова с выбором тоста.\\n5. Ночная сцена: белая ночь или огни города в зависимости от календаря.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "emotions", "title": "Эмоциональные выборы", "body": "- Игрок фиксирует чувства (гордость, ностальгия, надежда) в дневнике.\\n- Возможность пригласить спутников и делиться воспоминаниями.\\n- Поэты-андроиды цитируют Пушкина «Люблю тебя, Петра творенье».\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Имперская архитектура отражается в каналах, разводные мосты создают арки, а голографическая подсветка поддерживает\\nатмосферу прощального тура.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 2 500 XP и эмоциональный бафф «Прощание с культурной столицей».\\n- Ачивка «Петербургская душа» и персональный альбом воспоминаний.\\n- Повышение доверия у культурных фракций и доступ к VIP-проводам.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Сбор фото- и эмоций в персональный журнал.\\n- Временные окна: каждое событие доступно в определённый час.\\n- Влияние выбранных эмоций на финальную катсцену.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест предшествует финалу «Вечный Город» и закрывает линию Санкт-Петербурга, фиксируя отношения с NPC и подготовку\\nк Legacy-выбору.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Прощай, Петербург» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

