-- Quest: canon-quest-spb-2061-2077-catherine-palace-ball
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2061-2077-catherine-palace-ball', 'Санкт-Петербург 2061-2077 — «Бал у Екатерины»', 'Игрок готовится к историческому приёму, соблюдает этикет XVIII века и получает доступ к императорскому кругу.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2061-2077-catherine-palace-ball", "title": "Санкт-Петербург 2061-2077 — «Бал у Екатерины»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "culture", "imperial"], "topics": ["city_quests", "high_society"], "related_systems": ["narrative-service", "social-system", "progression-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2061-2077/quest-036-catherine-palace-ball.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Бал в Екатерининском дворце оформлен только текстово и не подключён к системам социального прогресса.", "goal": "Перенести реконструкцию императорского бала в структуру знаний с учётом этикета, экономики и социальных баффов.", "essence": "Игрок готовится к историческому приёму, соблюдает этикет XVIII века и получает доступ к императорскому кругу.", "key_points": ["Полноценная реконструкция императорского бала с костюмами, танцами и французским протоколом.", "Интеграция расходов на костюм и проверок этикета в социальные механики.", "Диалог с ИИ-Екатериной задаёт сюжетные ветки и эксклюзивные титулы."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Игрок получает приглашение на вечер в Екатерининском дворце и должен соответствовать церемониалу эпохи Просвещённого\\nабсолютизма, чтобы закрепить статус в высшем обществе.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получение приглашения и проверка происхождения игрока.\\n2. Подготовка костюма и обучение танцам XVIII века.\\n3. Мини-игры по этикету и обращению при дворе.\\n4. Танцевальные сцены с NPC и дипломатические разговоры.\\n5. Аудиенция у ИИ-Екатерины с дальнейшим выбором покровительства.\\n", "mechanics_links": ["mechanics/social/social-etiquette.yaml"], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Золотая анфилада и зеркальные залы Екатерининского дворца.\\n- Париковые и кринолиновые образы, пудровые гримы и веера.\\n- Французский язык в репликах придворных и императрицы.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- XP: 4 000, Валюта: -30 000 (затраты на костюм и подготовку).\\n- Социальный бафф «Императорский двор» +15 и титул «Фаворит Екатерины».\\n- Открытие высокостатусных сюжетных веток и сетевых контактов.\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Проверки этикета и харизмы на разных этапах приёма.\\n- Интеграция расходов и социального статуса в экономическую модель.\\n- Адаптивные диалоги с ИИ-императрицей, влияющие на будущие квесты.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Поддерживает императорскую линию Петербурга и связана с квестами о Царском Селе и международными дипломатическими событиями.\\nУспех открывает доступ к элитным событиям сезона.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Бал у Екатерины» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

