INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tashkent-plov-center', 'Ташкент 2020-2029 — «Центр плова»', 'Игрок наблюдает приготовление в казанах, пробует легендарный плов и повышает выносливость команды.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tashkent-plov-center", "title": "Ташкент 2020-2029 — «Центр плова»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["tashkent", "plov", "gastronomy"], "topics": ["culinary-experience", "cultural-practice"], "related_systems": ["narrative-service", "economy-system", "buff-system"], "related_documents": [{"id": "canon-region-cis-tashkent-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/tashkent/2020-2029/quest-002-plov-center.md", "visibility": "internal", "audience": ["narrative", "quest-design", "economy"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown не отображал масштаб казанов и баффы после дегустации плова.", "goal": "Структурировать экскурсию в Центр плова с акцентом на гастрономический опыт и экономику блюда.", "essence": "Игрок наблюдает приготовление в казанах, пробует легендарный плов и повышает выносливость команды.", "key_points": ["Казаны до тонны — производственная мощность и шоу для туристов.", "Дегустация разных рецептов усиливает баффы HP и Stamina.", "Квест поддерживает линию узбекской кухни и систему питания."]}, "content": {"sections": [{"id": "preparation", "title": "Утреннее приготовление", "body": "Экскурсия начинается утром, когда мастера закладывают рис, мясо и морковь.\\nИгрок фиксирует рецепт и помогает добавлять специи.\\n", "mechanics_links": [], "assets": []}, {"id": "tasting", "title": "Дегустация", "body": "Под руководством повара игрок пробует три сорта плова (3, 5, 20 лет традиции).\\nВыбор гарниров влияет на величину баффа «Plov Strength».\\n", "mechanics_links": [], "assets": []}, {"id": "culture", "title": "Истории и легенды", "body": "Гид рассказывает про «томление» и легенду Черчилля о коньяке и плове.\\nИгрок решает, инвестировать ли в обучение мастера для ресторана.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 800 XP, −20 кредов, бафф «Plov Strength» (+25% HP, +25% Stamina на 10 часов).\\n- Ачивка «Ошман» и +5 репутации гастрономической линии.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Плов (ош)", "definition": "Национальное блюдо Узбекистана из риса, мяса и специй, готовится в крупных казанах."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Центр плова» в knowledge-entry и описание гастрономических баффов."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

