INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-astana-2020-2029-steppe-winters', 'Астана 2020-2029 — «Степные зимы»', 'Игрок переживает зиму Астаны, балансируя тепло, здоровье и ресурсы во время ледяных ветров степи.', 'main', 6, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "experience_winter_onset", "text": "Пережить падение температуры до -40°C, получить расписание прогнозов и советы местных жителей", "type": "interact", "target": "astana_steppe_winter_onset", "count": 1, "optional": false}, {"id": "survive_blizzard", "text": "Выйти в буран, управлять ресурсами тепла, применять напитки и временные укрытия", "type": "perform", "target": "astana_steppe_winter_blizzard", "count": 1, "optional": false}, {"id": "find_shelter", "text": "Найти укрытие в зданиях, участвовать в мини-сценах общения, горячем питании и прогреве экипировки", "type": "travel", "target": "astana_steppe_winter_shelter", "count": 1, "optional": false}, {"id": "adapt_to_cold", "text": "Приобрести казахские шапки, тюбетейки и утеплённые куртки, получить бафф устойчивости к холоду", "type": "acquire", "target": "astana_steppe_winter_adaptation", "count": 1, "optional": false}]'::jsonb, '{"experience": 3000, "currency": 0, "items": [{"id": "cold_resistance_buff", "name": "Бафф устойчивости к холоду +20", "quantity": 1}], "reputation": {"astana_survival": 20}, "flags": ["steppe_winter_conqueror_achievement"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-astana-2020-2029-steppe-winters", "title": "Астана 2020-2029 — «Степные зимы»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["astana", "quest", "survival"], "topics": ["extreme-weather", "endurance"], "related_systems": ["narrative-service", "survival-service", "clothing-service"], "related_documents": [{"id": "github-issue-912", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/912", "relation": "migrated_to", "migrated_at": "2025-11-23T01:20:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/astana/2020-2029/quest-005-steppe-winters.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Игрокам не хватало экстремального климатического испытания в столице Казахстана.", "goal": "Смоделировать выживание при -40°C с буранами, одеждой и социальными сценами адаптации.", "essence": "Игрок переживает зиму Астаны, балансируя тепло, здоровье и ресурсы во время ледяных ветров степи.", "key_points": ["Буран и обморожение создают риск-менеджмент.", "Локальные NPC обучают традиционным способам защиты от холода.", "Квест завершается приобретением национальной экипировки и баффами выносливости."]}, "quest_definition": {"quest_type": "main", "level_min": 6, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "experience_winter_onset", "text": "Пережить падение температуры до -40°C, получить расписание прогнозов и советы местных жителей", "type": "interact", "target": "astana_steppe_winter_onset", "count": 1, "optional": false}, {"id": "survive_blizzard", "text": "Выйти в буран, управлять ресурсами тепла, применять напитки и временные укрытия", "type": "perform", "target": "astana_steppe_winter_blizzard", "count": 1, "optional": false}, {"id": "find_shelter", "text": "Найти укрытие в зданиях, участвовать в мини-сценах общения, горячем питании и прогреве экипировки", "type": "travel", "target": "astana_steppe_winter_shelter", "count": 1, "optional": false}, {"id": "adapt_to_cold", "text": "Приобрести казахские шапки, тюбетейки и утеплённые куртки, получить бафф устойчивости к холоду", "type": "acquire", "target": "astana_steppe_winter_adaptation", "count": 1, "optional": false}], "rewards": {"experience": 3000, "currency": 0, "items": [{"id": "cold_resistance_buff", "name": "Бафф устойчивости к холоду +20", "quantity": 1}], "reputation": {"astana_survival": 20}, "flags": ["steppe_winter_conqueror_achievement"]}, "branches": []}, "content": {"sections": [{"id": "onset", "title": "Начало зимы", "body": "Температура падает до -40°C, включается климатический модификатор. Игрок получает расписание прогнозов и советы местных жителей.\\n", "mechanics_links": ["mechanics/survival/extreme-cold.yaml"], "assets": []}, {"id": "exposure", "title": "Выход в буран", "body": "Ветры степи снижают обзор, дыхание обледеневает. Игрок управляет ресурсами тепла, применяет напитки и временные укрытия.\\n", "mechanics_links": ["mechanics/environment/blizzard.yaml"], "assets": []}, {"id": "shelter", "title": "Поиск укрытия", "body": "В зданиях доступны мини-сцены общения, горячее питание и прогрев экипировки. Решения влияют на мораль и здоровье.\\n", "mechanics_links": ["mechanics/survival/shelter-management.yaml"], "assets": []}, {"id": "adaptation", "title": "Адаптация", "body": "Игрок приобретает казахские шапки, тюбетейки и утеплённые куртки. Появляется бафф устойчивости к холоду.\\n", "mechanics_links": ["mechanics/economy/gear-upgrade.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 3 000 XP, устойчивость +20, ачивка «Покоритель степных зим».\\n- Доступ к сезонным эвентам Астаны и скидкам на зимнюю экипировку.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест интегрируется с климатическими сюжетами Казахстана, зимними фестивалями и инфраструктурой города.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 912, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики экстремальной погоды и выносливости."}, {"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Степные зимы» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

