-- Quest: canon-quest-spb-2061-2077-isaac-cathedral-climb
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2061-2077-isaac-cathedral-climb', 'Санкт-Петербург 2061-2077 — «Восхождение на Исаакий»', 'Игрок поднимается на колоннаду Исаакиевского собора, открывая панораму Петербурга и получая эстетический бафф.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2061-2077-isaac-cathedral-climb", "title": "Санкт-Петербург 2061-2077 — «Восхождение на Исаакий»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "tourism", "panorama"], "topics": ["city_quests", "cultural_experience"], "related_systems": ["narrative-service", "exploration-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2061-2077/quest-038-isaac-cathedral-climb.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Туристический квест по подъёму на Исаакиевский собор не интегрирован в систему исследовательских событий.", "goal": "Стандартизировать маршрут восхождения с учётом визуальных точек, наград и атмосферных эффектов.", "essence": "Игрок поднимается на колоннаду Исаакиевского собора, открывая панораму Петербурга и получая эстетический бафф.", "key_points": ["Восхождение по 262 ступеням с ощущением высоты и ветра.", "Панорамный обзор ключевых достопримечательностей города.", "Награды сфокусированы на эстетике и культурном опыте."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Короткий атмосферный квест для туристической линии: игрок посещает Исаакиевский собор, проходит проверку на выносливость\\nи получает 360-градусный вид на центр Петербурга.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Покупка билета и ознакомление с историей собора.\\n2. Подъём по винтовой лестнице с промежуточными остановками.\\n3. Выход на колоннаду и активация AR-панорамы города.\\n4. Наблюдение заката и фиксация воспоминаний.\\n", "mechanics_links": ["mechanics/exploration/vertical-navigation.yaml"], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Золотой купол и статуи апостолов в непосредственной близости.\\n- Ветровые потоки и звуки города на высоте 43 метров.\\n- AR-подсветка ключевых объектов: Адмиралтейство, Эрмитаж, Нева, Петропавловская крепость.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- XP: 1 500, Валюта: -50.\\n- Эстетический бафф +15 и ачивка «На золотом куполе».\\n- Открытие фотозон и романтических событий на высоте.\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Проверки выносливости и устойчивости при подъёме.\\n- AR-навигация по достопримечательностям в панорамном режиме.\\n- Социальные сценарии для кооперативных посещений.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Поддерживает туристическую и романтическую линии Петербурга, связана с квестами о культурном наследии и смотровых площадках.\\nУспех открывает скидки и ивенты на других высотных локациях города.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Восхождение на Исаакий» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

