-- Quest: quest-spb-2077-museum-city
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-spb-2077-museum-city', 'Санкт-Петербург 2061-2077 — Город-музей', 'Игрок выполняет обязанности хранителя живого музея, собирает культурный паспорт и получает работу гида.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-spb-2077-museum-city", "title": "Санкт-Петербург 2061-2077 — Город-музей", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "museum", "culture"], "topics": ["heritage", "education"], "related_systems": ["narrative-service", "tourism-service"], "related_documents": [{"id": "quest-spb-cultural-capital", "relation": "continues"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2061-2077/quest-031-museum-city.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Не хватало квеста, консервирующего Петербург как единый музей и связывающего главные объекты культурного наследия.", "goal": "Провести игрока по пятнадцати ключевым локациям исторического центра, выдавая знания и статус хранителя.", "essence": "Игрок выполняет обязанности хранителя живого музея, собирает культурный паспорт и получает работу гида.", "key_points": ["Исторический центр позиционируется как музей под открытым небом.", "Маршрут охватывает дворцы, соборы и крепости с AR-экскурсиями.", "Завершение даёт титул и постоянный доход экскурсовода."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Власти Петербурга объявили весь исторический центр «живым музеем»: игрок оформляется хранителем, изучает объекты\\nвсемирного наследия и помогает поддерживать культурное значение города.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить назначение хранителем городского музея.\\n2. Пройти маршрут по пятнадцати объектам: Эрмитаж, Русский музей, Кунсткамера, Петропавловская крепость, Исаакиевский\\n   собор, Казанский собор, Спас на Крови, Михайловский замок, Мариинский театр, Смольный, Александро-Невская лавра,\\n   Строгановский дворец, Юсуповский дворец, Адмиралтейство, Сенат и Синод.\\n3. На каждом объекте активировать AR-экскурсию с историческим нарративом.\\n4. Собрать отметки в «Культурном паспорте».\\n5. Получить титул и лицензию официального экскурсовода.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Петербург остаётся объектом всемирного наследия: более 2 300 дворцов и зданий признаны экспонатами, а AR-инфраструктура\\nпозволяет погружать гостей в исторические пласты города.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 5 000 XP.\\n- Доход: контракт гида на 10 000 еддис в неделю.\\n- Атрибут «Культура» +30.\\n- Титул «Хранитель Петербурга».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- AR-экскурсии и образовательные модули для каждого объекта.\\n- Сбор отметок в культурном паспорте.\\n- Социальные события с туристами и NPC-гостями.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Продолжает культурную линию Петербурга и открывает доступ к мероприятиям «Культурная столица».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Город-музей» в формат знаний Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

