INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-astana-2029-futuristic-city', 'Астана 2020-2029 — Город будущего', 'Игрок следует по маршруту от Байтерека до Abu Dhabi Plaza, фиксирует контрасты и формирует материалы для туров LiveOps.', 'main', 5, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "get_tour_briefing", "text": "Получить бриф от городского гида и построить маршрут по Байтереку, Хан Шатыр и Дворцу мира", "type": "interact", "target": "astana_futuristic_city_briefing", "count": 1, "optional": false}, {"id": "photograph_from_baiterek", "text": "Сделать панорамные фото с Байтерека, отмечая контраст степи и стеклянных небоскрёбов", "type": "craft", "target": "astana_futuristic_city_baiterek_photos", "count": 1, "optional": false}, {"id": "visit_khan_shatyr", "text": "Посетить Хан Шатыр, изучить внутренний парк и торговые уровни", "type": "explore", "target": "astana_futuristic_city_khan_shatyr", "count": 1, "optional": false}, {"id": "meet_architects", "text": "Зайти в Дворец мира и согласия, провести встречу с архитекторами и собрать истории о строительстве", "type": "social", "target": "astana_futuristic_city_architects", "count": 1, "optional": false}, {"id": "prepare_media_package", "text": "Завершить маршрут у Abu Dhabi Plaza, подготовить медиапакет и рекомендации для LiveOps туров", "type": "craft", "target": "astana_futuristic_city_media_package", "count": 1, "optional": false}]'::jsonb, '{"experience": 2000, "currency": 0, "items": [], "reputation": {"astana_tourism": 20}, "flags": ["future_researcher_achievement"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-astana-2029-futuristic-city", "title": "Астана 2020-2029 — Город будущего", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["astana", "architecture", "futurism"], "topics": ["architecture", "city-tour"], "related_systems": ["narrative-service", "travel-service", "liveops-events-service"], "related_documents": [{"id": "github-issue-917", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/917", "relation": "migrated_to", "migrated_at": "2025-11-23T01:15:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/astana/2020-2029/quest-010-futuristic-city.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Архитектурная ветка Астаны требовала целостного квеста, демонстрирующего, как мегаполис вырос с нуля за два десятилетия.", "goal": "Провести игрока по ключевым футуристичным достопримечательностям, сравнив их с окружающей степью и раскрывая историю города.", "essence": "Игрок следует по маршруту от Байтерека до Abu Dhabi Plaza, фиксирует контрасты и формирует материалы для туров LiveOps.", "key_points": ["Футуристичные здания Астаны создают уникальный силуэт на фоне бескрайней степи.", "Байтерек, Хан Шатыр, Дворец мира и Abu Dhabi Plaza отражают амбиции государства.", "Фото- и медиа-задания усиливают визуальный сторителлинг и подключают LiveOps-туры."]}, "quest_definition": {"quest_type": "main", "level_min": 5, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "get_tour_briefing", "text": "Получить бриф от городского гида и построить маршрут по Байтереку, Хан Шатыр и Дворцу мира", "type": "interact", "target": "astana_futuristic_city_briefing", "count": 1, "optional": false}, {"id": "photograph_from_baiterek", "text": "Сделать панорамные фото с Байтерека, отмечая контраст степи и стеклянных небоскрёбов", "type": "craft", "target": "astana_futuristic_city_baiterek_photos", "count": 1, "optional": false}, {"id": "visit_khan_shatyr", "text": "Посетить Хан Шатыр, изучить внутренний парк и торговые уровни", "type": "explore", "target": "astana_futuristic_city_khan_shatyr", "count": 1, "optional": false}, {"id": "meet_architects", "text": "Зайти в Дворец мира и согласия, провести встречу с архитекторами и собрать истории о строительстве", "type": "social", "target": "astana_futuristic_city_architects", "count": 1, "optional": false}, {"id": "prepare_media_package", "text": "Завершить маршрут у Abu Dhabi Plaza, подготовить медиапакет и рекомендации для LiveOps туров", "type": "craft", "target": "astana_futuristic_city_media_package", "count": 1, "optional": false}], "rewards": {"experience": 2000, "currency": 0, "items": [], "reputation": {"astana_tourism": 20}, "flags": ["future_researcher_achievement"]}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест знакомит игрока с «городом будущего»: нужно пройти по маршруту, который объединяет знаковые объекты, понять символику\\nкаждого здания и зафиксировать материал для городских туров.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить бриф от городского гида и построить маршрут по Байтереку, Хан Шатыр и Дворцу мира.\\n2. Сделать панорамные фото с Байтерека, отмечая контраст степи и стеклянных небоскрёбов.\\n3. Посетить Хан Шатыр, изучить внутренний парк и торговые уровни.\\n4. Зайти в Дворец мира и согласия, провести встречу с архитекторами и собрать истории о строительстве.\\n5. Завершить маршрут у Abu Dhabi Plaza, подготовить медиапакет и рекомендации для LiveOps туров.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Астана выросла за 20 лет и стала витриной футуристичной архитектуры Центральной Азии. Норман Фостер и другие звёздные\\nархитекторы спроектировали строения, подчёркивающие тему огня и света в степи.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 2 000 XP.\\n- Атрибут «Эстетика»: +20.\\n- Доступ к LiveOps-трум «Город будущего».\\n- Ачивка «Исследователь будущего».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Туристические маршруты, фото-документация, сюжетные брифы архитекторов и интеграция с медиаконтентом LiveOps.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает архитектурную линию Астаны с политическими и культурными сюжетами региона, открывая дополнительные туры и\\nивенты мегаполиса.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 917, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики архитектуры и городского тура."}, {"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Город будущего» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

