-- Quest: quest-spb-2077-nicholas-ii-ghost
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-spb-2077-nicholas-ii-ghost', 'Санкт-Петербург 2061-2077 — Призрак Николая II', 'Игрок общается с цифровым «призраком», погружается в последние дни империи и принимает решение о помощи семье Романовых.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-spb-2077-nicholas-ii-ghost", "title": "Санкт-Петербург 2061-2077 — Призрак Николая II", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "romanov-dynasty", "supernatural"], "topics": ["narrative", "historical-fiction"], "related_systems": ["narrative-service", "vr-service"], "related_documents": [{"id": "quest-spb-2077-romanov-treasure", "relation": "connected"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2061-2077/quest-034-nicholas-ii-ghost.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Требовалось драматическое продолжение императорской линии с личным взглядом Николая II.", "goal": "Организовать ночную встречу с ИИ-призраком в Александровском дворце и предложить игроку изменить ход истории.", "essence": "Игрок общается с цифровым «призраком», погружается в последние дни империи и принимает решение о помощи семье Романовых.", "key_points": ["Александровский дворец оживает через AR/VR-эффекты.", "Любой выбор влияет на трактовку легенды об Анастасии.", "Квест раскрывает эмоциональную сторону императорской ветки."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Ночной визит в Александровский дворец приводит к встрече с ИИ-реконструкцией Николая II. Игрок слушает его воспоминания\\nи узнаёт об альтернативной версии судьбы детей.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить допуск к ночной экскурсии в Александровском дворце.\\n2. В личных покоях активировать ИИ-проекцию Николая II.\\n3. Прослушать рассказ о последних днях империи и семье.\\n4. Погрузиться в VR-реконструкцию последнего года Романовых.\\n5. Принять решение о выполнении просьбы императора.\\n", "mechanics_links": [], "assets": []}, {"id": "choices", "title": "Варианты выбора", "body": "- Помочь: найти следы легендарной дочери и изменить историческое повествование.\\n- Отказать: прекратить вмешательство и сохранить нынешнюю версию истории.\\n- Манипулировать: использовать знания для собственной выгоды и политического давления.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Личные покои императорской семьи, семейные фотографии, детские комнаты и атмосфера трагедии подчёркивают культурное\\nнаследие Александровского дворца.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 5 000 XP.\\n- Доступ к потоку «Тайны Романовых».\\n- Эмоциональный трек и новое восприятие истории (повышение репутации в культурных фракциях).\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- ИИ-призрак и персонализированные диалоги.\\n- VR-реконструкция исторических событий.\\n- Моральный выбор с влиянием на дальнейшие квесты.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест продолжает сюжет о императорских сокровищах и влияет на линии, связанные с династией Романовых.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Призрак Николая II» в формат знаний Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

