INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-006-chicago-style-hot-dog', 'Чикаго — Chicago-Style Hot Dog', 'Игрок ищет легендарный стенд, заказывает правильный hot dog и получает бафф насыщения и статус местного гурмана.', 'side', 2, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "find_stand", "text": "Найти культовый стенд (например, Portillo''s или Maxwell Street Market)", "type": "travel", "target": "hot_dog_stand", "count": 1, "optional": false}, {"id": "order_classic", "text": "Заказать классический hot dog с обязательными ингредиентами: горчица, лук, relish, ломтики помидора, pickle spear, sport peppers и сельдерейная соль", "type": "interact", "target": "hot_dog_vendor", "count": 1, "optional": false}, {"id": "try_ketchup", "text": "Попробовать попросить кетчуп и получить реакцию NPC — сценка осуждения", "type": "dialogue", "target": "hot_dog_vendor", "count": 1, "optional": true}, {"id": "get_buff", "text": "Зафиксировать гастрономический бафф и открыть дополнительные точки стрит-фуда", "type": "interact", "target": "food_buff_station", "count": 1, "optional": false}]'::jsonb, '{"experience": 600, "money": {"type": "eddies", "value": -8}, "reputation": {"culinary": 10}, "unlocks": {"achievements": [{"id": "chicago_hot_dog_master", "name": "Чикагский Хот-Дог Мастер"}], "flags": []}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-006-chicago-style-hot-dog", "title": "Чикаго — Chicago-Style Hot Dog", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T11:15:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["chicago", "cuisine", "street-food"], "topics": ["timeline_author", "regional_quests"], "related_systems": ["quest-engine", "economy-service", "social-service"], "related_documents": [{"id": "questline-chicago-culinary", "relation": "collection"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/chicago/2020-2029/quest-006-chicago-style-hot-dog.md", "visibility": "internal", "audience": ["concept", "narrative", "events"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Гастрономический квест про знаменитый чикагский хот-дог описан в заметочной форме и не интегрирован в региональную линейку.", "goal": "Структурировать опыт дегустации, подчеркнув «семь топпингов без кетчупа» и влияние на городскую экономику.", "essence": "Игрок ищет легендарный стенд, заказывает правильный hot dog и получает бафф насыщения и статус местного гурмана.", "key_points": ["Обязательные семь топпингов без кетчупа на булке с маком.", "Локации Portillo''s, уличные стенды и социальные реакции продавцов.", "Связь с кулинарной веткой и экономикой стрит-фуда."]}, "quest_definition": {"quest_type": "side", "level_min": 2, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "find_stand", "text": "Найти культовый стенд (например, Portillo''s или Maxwell Street Market)", "type": "travel", "target": "hot_dog_stand", "count": 1, "optional": false}, {"id": "order_classic", "text": "Заказать классический hot dog с обязательными ингредиентами: горчица, лук, relish, ломтики помидора, pickle spear, sport peppers и сельдерейная соль", "type": "interact", "target": "hot_dog_vendor", "count": 1, "optional": false}, {"id": "try_ketchup", "text": "Попробовать попросить кетчуп и получить реакцию NPC — сценка осуждения", "type": "dialogue", "target": "hot_dog_vendor", "count": 1, "optional": true}, {"id": "get_buff", "text": "Зафиксировать гастрономический бафф и открыть дополнительные точки стрит-фуда", "type": "interact", "target": "food_buff_station", "count": 1, "optional": false}], "rewards": {"experience": 600, "money": {"type": "eddies", "value": -8}, "reputation": {"culinary": 10}, "unlocks": {"achievements": [{"id": "chicago_hot_dog_master", "name": "Чикагский Хот-Дог Мастер"}], "flags": []}}, "branches": []}, "content": {"sections": [{"id": "introduction", "title": "Введение", "body": "Chicago-style hot dog — символ уличной культуры Чикаго. Булка с маковыми семечками, говяжья сосиска и семь топпингов формируют уникальный вкус, который игрокам предстоит испытать в рамках кулинарной петли города.\\n", "mechanics_links": ["mechanics/social/tourism-system.yaml"], "assets": []}, {"id": "regional_context", "title": "Исторические сведения", "body": "- Выражение «dragged through the garden» описывает изобилие овощей в хот-доге.\\n- Рецепт закрепился в эпоху Великой депрессии как доступная, но сытная еда.\\n- Запрет на кетчуп стал частью городского фольклора.\\n", "mechanics_links": ["canon/lore/_03-lore/timeline-author/quests/america/chicago/chicago-culinary.yaml"], "assets": []}, {"id": "quest_flow", "title": "Этапы квеста", "body": "1. Найти культовый стенд (например, Portillo''s или Maxwell Street Market).\\n2. Заказать классический hot dog с обязательными ингредиентами: горчица, лук, relish, ломтики помидора, pickle spear, sport peppers и сельдерейная соль.\\n3. Получить реакцию NPC, если попытаться попросить кетчуп — сценка осуждения.\\n4. Зафиксировать гастрономический бафф и открыть дополнительные точки стрит-фуда.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 600 XP, −8 валюты, бафф HP +15% на 4 часа.\\n- Ачивка «Чикагский Хот-Дог Мастер».\\n- Повышение показателей кулинарной привлекательности района.\\n", "mechanics_links": ["mechanics/progression/rewards-catalog.yaml"], "assets": []}, {"id": "immersion", "title": "Атмосфера и связи", "body": "Квест подчёркивает стрит-фуд культуру города и может запускать события с конкурсами хот-догов и вызовами «без кетчупа». Связан с кулинарной линией Чикаго и экономикой гастрономических ярмарок.\\n", "mechanics_links": ["canon/lore/_03-lore/timeline-author/quests/america/chicago/2020-2029/index.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1188, "needs_task": false, "queue_reference": ["shared/trackers/queues/vision/review.yaml#quest-chicago-2029-006"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-09", "author": "concept_director", "changes": "Структурирован квест по дегустации чикагского hot dog."}, {"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлен quest_definition с полной структурой квеста согласно архитектуре системы квестов."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

