INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-baku-2029-flame-towers', 'Баку 2020-2029 — Пламенные башни', 'Игрок исследует башни, наблюдает LED-шоу и создаёт медиапакет для ночных экскурсий.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-baku-2029-flame-towers", "title": "Баку 2020-2029 — Пламенные башни", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["baku", "flame-towers", "nightlife"], "topics": ["architecture", "tourism"], "related_systems": ["narrative-service", "travel-service", "liveops-events-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/baku/2020-2029/quest-001-flame-towers.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Архитектурная линия Баку требовала вступительного квеста, объясняющего символику Flame Towers и их роль в имидже города.", "goal": "Показать комплекс днём и ночью, раскрыть историю «земли огня» и подчеркнуть туристические сценарии.", "essence": "Игрок исследует башни, наблюдает LED-шоу и создаёт медиапакет для ночных экскурсий.", "key_points": ["Фасады башен имитируют пламя благодаря динамической LED-системе.", "Квест соединяет современный skyline Баку со старым городом и Каспием.", "Ночные события расширяют туристические LiveOps-активности."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест знакомит игрока с комплексом Flame Towers: нужно оценить архитектуру днём, подготовиться к вечернему шоу и собрать\\nматериалы о бренде «земли огня».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Встретиться с гидом у подножия башен и изучить историю строительства.\\n2. Обследовать интерьер и смотровые площадки, зафиксировать панораму Каспийского побережья.\\n3. Подготовить оборудование для ночного съёмочного блока.\\n4. Наблюдать LED-шоу, снять таймлапс и собрать реакции туристов.\\n5. Собрать данные о влиянии комплекса на ночную экономику и запустить LiveOps-рекламный пакет.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Flame Towers символизируют исторический культ огня Азербайджана и современную нефтегазовую мощь. Световые анимации\\nпереводят мотив пламени в городской бренд.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 800 XP.\\n- Расходы: -20 еддис.\\n- Атрибут «Эстетика»: +10.\\n- Доступ к ночным экскурсиям и ачивка «Хранитель пламени».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Туристические маршруты, фото- и видеофиксация, ночные LiveOps-события, социальные опросы гостей.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает архитектурные и исторические линии Баку, открывая миссии старого города и природного наследия «земли огня».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 898, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Пламенные башни» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

