-- Quest: canon-quest-spb-2078-2093-hermitage-cats
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2078-2093-hermitage-cats', 'Санкт-Петербург 2078-2093 — «Коты Эрмитажа»', 'Игрок помогает легендарным котам бороться с кибер-крысами, организует День кота и получает дружбу пушистых хранителей музея.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2078-2093-hermitage-cats", "title": "Санкт-Петербург 2078-2093 — «Коты Эрмитажа»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "slice_of_life"], "topics": ["museum-life", "wholesome-content"], "related_systems": ["narrative-service", "social-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2078-2093/quest-043-hermitage-cats.md", "visibility": "internal", "audience": ["concept", "narrative", "community"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сюжет про котов Эрмитажа оставался в Markdown и не участвовал в социальной программе города.", "goal": "Перенести милый квест в YAML, описать взаимодействие с котами и их поддержку игроков.", "essence": "Игрок помогает легендарным котам бороться с кибер-крысами, организует День кота и получает дружбу пушистых хранителей музея.", "key_points": ["Экскурсия в подвалы Эрмитажа и знакомство с индивидуальными котами.", "Мини-игра по борьбе с кибер-крысами и организация праздника.", "Долгосрочный бафф от котов, отражённый в социальных механиках."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Игрок спускается в подвалы Эрмитажа, знакомится с котами-охранниками и помогает им сохранить музей от\\nтехнологичных грызунов. Квест раскрывает традицию, продолжающуюся с XVIII века.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Исследовать подвалы и познакомиться с котами, каждый из которых имеет характер и историю.\\n2. Очистить туннели от кибер-крыс, используя гаджеты и помощь котов.\\n3. Организовать «День кота» с участием туристов и стриминговых платформ.\\n4. Присвоить котам статус «Хранители Культуры» и открыть им доступ в экспозиции.\\n", "mechanics_links": ["mechanics/social/player-orders-via-npc.yaml"], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Подвалы дворца наполнены историческими артефактами, мисками с именами котов и туристами, которые приходят на\\nэкскурсии. Упоминания Екатерины II и традиций XVIII века поддерживают связь с историей.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1 000 XP и любовь котов (+100 к скрытому параметру «Коты»).\\n- Ачивка «Друг Котов Эрмитажа» и случайные баффы от питомцев.\\n- Социальный статус в музее и доступ к эксклюзивным экскурсиям.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Wholesome контент и социальные сцены.\\n- Мини-игры против кибер-крыс.\\n- Система питомцев, дающих временные бонусы.\\n", "mechanics_links": ["mechanics/social/family-relationships-system.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест продолжает линию Эрмитажа, дополняя сюжет «Ограбление Эрмитажа» дружбой с котами и открывая социальные\\nколлаборации музея.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Коты Эрмитажа» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

