INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-baku-2029-old-city', 'Баку 2020-2029 — Старый город Ичери Шехер', 'Игрок исследует лабиринт улиц, общается с ремесленниками и фиксирует легенды старого города для туристических программ.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-baku-2029-old-city", "title": "Баку 2020-2029 — Старый город Ичери Шехер", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["baku", "icheri-sheher", "heritage"], "topics": ["cultural-heritage", "urban-history"], "related_systems": ["narrative-service", "travel-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/baku/2020-2029/quest-002-old-city-icheri-sheher.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Историческая линия Баку нуждалась в квесте, который передаёт атмосферу крепостного квартала Ичери Шехер.", "goal": "Провести игрока по Девичьей башне, дворцу Ширваншахов и ремесленным улицам, сохранив контраст со стеклянным центром города.", "essence": "Игрок исследует лабиринт улиц, общается с ремесленниками и фиксирует легенды старого города для туристических программ.", "key_points": ["Ичери Шехер — объект наследия UNESCO с сохранившимися стенами и башнями.", "Девичья башня и дворец Ширваншахов раскрывают символы бакинской идентичности.", "Квест формирует LiveOps-тур, объединяющий историю, гастрономию и ремёсла."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест приглашает игрока попасть внутрь Ичери Шехер, почувствовать узкие улочки и собрать истории, связывающие средневековый\\nквартал с современным мегаполисом.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Войти через крепостные ворота и получить карту маршрута.\\n2. Посетить Девичью башню, провести фото- и аудиосессии с гидом.\\n3. Пройти по улице ремесленников, собрать материалы о традиционных товарах.\\n4. Исследовать дворец Ширваншахов, понять архитектуру зала и мавзолея.\\n5. Провести вечернюю экскурсию с подсветкой стен и подготовить отчёт для туристического портала.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Ичери Шехер окружён крепостными стенами XII века, улицы выложены известняком, а квартал контрастирует с современными\\nнебоскрёбами на набережной Баку.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 500 XP.\\n- Расходы: -10 еддис.\\n- Атрибут «Культура»: +15.\\n- Ачивка «Хранитель Ичери Шехер».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Исторические экскурсии, сбор легенд, взаимодействие с ремесленниками и фото-квесты для LiveOps.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест соединяет историческую линию Баку с архитектурными и гастрономическими сюжетами, открывая фестивали наследия.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 899, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Старый город Ичери Шехер» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::j