INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tashkent-silk-and-spices', 'Ташкент 2020-2029 — «Шёлк и специи»', 'Игрок собирает товары на Чорсу, ведёт караван через Самарканд, Бухару и Хиву, торгуясь и преодолевая угрозы.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tashkent-silk-and-spices", "title": "Ташкент 2020-2029 — «Шёлк и специи»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["tashkent", "silk-road", "trade"], "topics": ["adventure", "economy"], "related_systems": ["narrative-service", "economy-system", "reputation-system"], "related_documents": [{"id": "canon-region-cis-tashkent-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/tashkent/2020-2029/quest-010-silk-and-spices.md", "visibility": "internal", "audience": ["narrative", "quest-design", "economy"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сценарий каравана не раскрывал экономические механики и риски путешествия.", "goal": "Смоделировать торговое путешествие по Шёлковому пути с принятием решений и балансом прибыли.", "essence": "Игрок собирает товары на Чорсу, ведёт караван через Самарканд, Бухару и Хиву, торгуясь и преодолевая угрозы.", "key_points": ["Необходимо выбирать охрану, маршрут и логистику.", "Встречи с торговцами и разбойниками влияют на прибыль.", "Финал оценивает, насколько эффективно игрок управлял ресурсами."]}, "content": {"sections": [{"id": "preparation", "title": "Подготовка каравана", "body": "Игрок закупает товары, распределяет вес между верблюдами и нанимает охрану.\\n", "mechanics_links": [], "assets": []}, {"id": "route", "title": "Маршрут по городам", "body": "- Самарканд: керамика.\\n- Бухара: специи.\\n- Хива: шёлк.\\nКаждая остановка содержит мини-задания по торгу.\\n", "mechanics_links": [], "assets": []}, {"id": "encounters", "title": "События пути", "body": "Игрок сталкивается с песчаными бурями, разбойниками и налоговыми сборщиками.\\n", "mechanics_links": [], "assets": []}, {"id": "market", "title": "Продажа товаров", "body": "В Ташкенте игрок продаёт товары, рассчитывает прибыль и решает, инвестировать ли в экспансию.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 5 000 XP.\\n- Прибыль: 30 000 едди.\\n- Репутация «Торговцы» +10.\\n- Ачивка «Торговец Шёлкового пути».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Караван-сарай", "definition": "Постоялый двор на торговых путях, обеспечивающий отдых и безопасность каравана."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Шёлк и специи» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

