INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-baku-2029-yanar-dag', 'Баку 2020-2029 — Янар Даг', 'Игрок наблюдает пламя, общается с хранителем локации и готовит материалы для духовных и туристических сценариев.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-baku-2029-yanar-dag", "title": "Баку 2020-2029 — Янар Даг", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["baku", "yanar-dag", "natural-fire"], "topics": ["natural-wonders", "spirituality"], "related_systems": ["narrative-service", "travel-service", "liveops-events-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/baku/2020-2029/quest-003-yanar-dag.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Природная линия Азербайджана нуждалась в квесте, который раскрывает феномен вечного огня Янар Даг и его сакральный контекст.", "goal": "Показать поездку к горящему холму, объяснить природный механизм и связь с зороастрийскими традициями.", "essence": "Игрок наблюдает пламя, общается с хранителем локации и готовит материалы для духовных и туристических сценариев.", "key_points": ["Огонь поддерживается естественным выходом газа и практически не гаснет.", "Локация связана с древними культами огня и современным брендом «земли огня».", "Ночные визиты создают сильный визуальный эффект и мотивируют LiveOps-мероприятия."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест отправляет игрока из Баку к Янар Даг. Нужно организовать транспорт, подготовиться к ночному наблюдению и собрать\\nистории о поклонении огню.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить допуск в охраняемую зону и организовать поездку из города.\\n2. Провести дневную разведку, изучить безопасность и структуру грунта.\\n3. Вернуться ночью, наблюдать пламя, зафиксировать температуру и визуальный эффект.\\n4. Встретиться с местным хранителем, собрать легенды и рассказ о зороастрийцах.\\n5. Подготовить медиапакет и предложить сезонный LiveOps-фестиваль огня.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Янар Даг расположен в 25 км от Баку на Апшеронском полуострове. Природный газ выходит через трещины, формируя непрерывное\\nпламя, которое почитали зороастрийцы и современные паломники.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 500 XP.\\n- Атрибут «Природа»: +10.\\n- Ачивка «Огнепоклонник».\\n- Доступ к LiveOps-событию «Вечный огонь».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Экологические исследования, духовные интервью, ночные события и производство медиа для LiveOps.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает природную линию Азербайджана с архитектурными и туристическими сюжетами, открывая цепочки о культуре огня.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 900, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Янар Даг» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

