INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tbilisi-khinkali-master', 'Тбилиси 2020-2029 — «Мастер хинкали»', 'Игрок изучает технику поедания хинкали и участвует в челлендже за звание «Хинкальный король».', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tbilisi-khinkali-master", "title": "Тбилиси 2020-2029 — «Мастер хинкали»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["tbilisi", "cuisine", "khinkali"], "topics": ["culinary", "social"], "related_systems": ["narrative-service", "culinary-system", "social-system"], "related_documents": [{"id": "canon-region-cis-tbilisi-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/tbilisi/2020-2029/quest-002-khinkali-master.md", "visibility": "internal", "audience": ["narrative", "quest-design", "live_ops"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о хинкали не фиксировал механику обучения и соревнования за количество съеденных порций.", "goal": "Формализовать дегустацию и соревновательный элемент с баффами выносливости.", "essence": "Игрок изучает технику поедания хинкали и участвует в челлендже за звание «Хинкальный король».", "key_points": ["Нужен контроль температуры и аккуратности, чтобы не пролить бульон.", "Хвостики считаются очками, определяющими победителя.", "Соревнование усиливает социальные связи и кулинарную репутацию Тбилиси."]}, "content": {"sections": [{"id": "introduction", "title": "Встреча в хинкальной", "body": "Игрок попадает в популярную хинкальную, где шеф рассказывает историю блюда.\\nВыбор уровня сложности влияет на количество попыток в мини-игре.\\n", "mechanics_links": [], "assets": []}, {"id": "technique", "title": "Техника поедания", "body": "- Практика: держать за хвостик, надкусывать и пить бульон.\\n- Механика точности предотвращает потерю очков.\\n", "mechanics_links": [], "assets": []}, {"id": "competition", "title": "Соревнование", "body": "Игрок соревнуется с NPC: кто съест больше хинкали без проливов.\\nХвостики складываются на тарелке и конвертируются в очки.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 800 XP.\\n- Издержки: −20 кредов.\\n- Бафф «+25% HP на 10 часов».\\n- Ачивка «Хинкальный король».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Хинкали", "definition": "Грузинские пельмени с бульоном, которые едят руками, оставляя хвостики."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Мастер хинкали» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

