INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tbilisi-supra-feast', 'Тбилиси 2020-2029 — «Грузинская супра»', 'Игрок участвует в семейной супре, слушает тамаду, поднимает тосты и укрепляет связи.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tbilisi-supra-feast", "title": "Тбилиси 2020-2029 — «Грузинская супра»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["tbilisi", "supra", "hospitality"], "topics": ["social", "cultural-tradition"], "related_systems": ["narrative-service", "social-system", "reputation-system"], "related_documents": [{"id": "canon-region-cis-tbilisi-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/tbilisi/2020-2029/quest-004-supra-feast.md", "visibility": "internal", "audience": ["narrative", "quest-design", "live_ops"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown-квест не описывал структуру супры и роль тамады как ведущего социального события.", "goal": "Задать сценарий грузинского застолья с тостами, песнями и социальными баффами.", "essence": "Игрок участвует в семейной супре, слушает тамаду, поднимает тосты и укрепляет связи.", "key_points": ["Тамада координирует длинные тосты и задаёт темы обсуждения.", "Гости пьют вино из кантси и делятся историями.", "Участие даёт долгосрочный бонус к социальным параметрам."]}, "content": {"sections": [{"id": "invitation", "title": "Приглашение на супру", "body": "Игрок получает пригласительный, выбирая подарки для хозяев.\\nОт выбора зависит настроение застолья и стартовые бонусы.\\n", "mechanics_links": [], "assets": []}, {"id": "feast", "title": "Застолье", "body": "- Стол ломится от блюд: хинкали, хачапури, пхали, сациви.\\n- Игра «найди блюдо» по рекомендациям хозяев.\\n", "mechanics_links": [], "assets": []}, {"id": "toast_master", "title": "Тамада и тосты", "body": "- Тамада задаёт темы: семья, предки, гости, будущее.\\n- Игрок должен импровизировать ответные тосты, влияющие на репутацию.\\n", "mechanics_links": [], "assets": []}, {"id": "singing", "title": "Пение и завершение", "body": "Общий хор поёт многоголосье; игрок выбирает партию и получает бонус за попадание в тональность.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 2 000 XP.\\n- Социальный параметр: +20.\\n- Бафф «Hospitality XP +50% на 48 часов».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Тамада", "definition": "Ведущий грузинского застолья, произносящий тосты и управляющий супрой."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Грузинская супра» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

