-- Quest: canon-quest-spb-2078-2093-cultural-league
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2078-2093-cultural-league', 'Санкт-Петербург 2078-2093 — «Культурная Лига»', 'Санкт-Петербург бросает вызов Москве, Лондону и Парижу, а игроки влияют на голосование через восстановление культурных объектов и презентации.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2078-2093-cultural-league", "title": "Санкт-Петербург 2078-2093 — «Культурная Лига»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "cultural_competition"], "topics": ["world-events", "seasonal-finals"], "related_systems": ["narrative-service", "liveops-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2078-2093/quest-041-cultural-league.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Финальный сезонный ивент «Культурная Лига» оставался в Markdown и не интегрирован в сезонную рамку города.", "goal": "Описать масштабное соревнование городов, где игроки восстанавливают музеи, проводят фестивали и борются за титул культурной столицы.", "essence": "Санкт-Петербург бросает вызов Москве, Лондону и Парижу, а игроки влияют на голосование через восстановление культурных объектов и презентации.", "key_points": ["Три этапа подготовки: реставрация музеев, фестиваль искусств, привлечение судей.", "Голосование игроков решает, какой город станет культурной столицей.", "Победа открывает туристические и экономические бонусы для региона."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "«Культурная Лига» завершает сезон и объединяет игроков в подготовке Санкт-Петербурга к глобальному состязанию.\\nГород взаимодействует с мировыми столицами, демонстрирует наследие и борется за звание культурной столицы мира.\\n", "mechanics_links": ["mechanics/world/events/live-events-system.yaml"], "assets": []}, {"id": "preparation", "title": "Подготовка", "body": "- Восстановить десять ключевых музеев и выставочных залов.\\n- Организовать фестиваль искусств с участием локальных и мировых звёзд.\\n- Сформировать совет международных судей и обеспечить безопасность мероприятия.\\n- Провести маркетинговые кампании, продвигающие город как «окно в Европу».\\n", "mechanics_links": ["mechanics/economy/economy-events.yaml"], "assets": []}, {"id": "competition", "title": "Конкуренция городов", "body": "Санкт-Петербург соперничает с Москвой, Лондоном и Парижем. Каждому городу сопоставлены уникальные особенности:\\nимперские дворцы и Эрмитаж, Кремль и Большой театр, Британский музей и королевская сцена, Лувр и романтические\\nмаршруты. Игроки готовят презентации и участвуют в дебатах.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "finale", "title": "Финал и награды", "body": "Во время голосования игроки и судьи выбирают культурную столицу. Победа Санкт-Петербурга приносит 15 000 XP,\\nтитул «Культурная Столица Мира» и глобальный туристический буст. Даже при поражении город укрепляет репутацию и\\nполучает 10 000 XP.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Соревнование городов с глобальными метриками.\\n- Презентации культурного наследия и взаимодействие с NPC-судьями.\\n- Механика голосования, зависящая от вклада гильдий и игроков.\\n", "mechanics_links": ["mechanics/social/player-orders-via-npc.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест продолжает культурную линию Санкт-Петербурга, связан с «Городом-Музеем» и «Культурной Столицей», а также\\nзавершает сезонную кампанию региона.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация сезонного события «Культурная Лига» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

