ontent": {"sections": [{"id": "restaurant", "title": "Посещение ресторана", "body": "Игрок находит авторитетное место, знакомится с шефом и изучает меню пловов.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "tasting", "title": "Дегустация плова", "body": "Подаются варианты с бараниной, каштанами и сухофруктами; игрок фиксирует эффекты wellbeing.\\n", "mechanics_links": ["mechanics/food/signature-dishes.yaml"], "assets": []}, {"id": "tradition", "title": "Казмаг и чай", "body": "Игрок учится готовить казмаг и завершает трапезу шафрановым чаем, получая локальные истории.\\n", "mechanics_links": ["mechanics/narrative/cultural-rituals.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 800 XP, -40 валюты, бафф +20% HP на 8 часов, ачивка «Мастер плова».\\n- Доступ к гастрономическим фестивалям и LiveOps кулинарным событиям.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает кулинарные и культурные линии Баку и поддерживает международные гастротуры.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 905, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Плов по-бакински» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

