INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tbilisi-polyphonic-singing', 'Тбилиси 2020-2029 — «Грузинское многоголосье»', 'Игрок посещает концерт, изучает историю полифонии и пробует спеть одну из партий хора.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tbilisi-polyphonic-singing", "title": "Тбилиси 2020-2029 — «Грузинское многоголосье»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["tbilisi", "polyphony", "music"], "topics": ["cultural-heritage", "performance"], "related_systems": ["narrative-service", "culture-system", "reputation-system"], "related_documents": [{"id": "canon-region-cis-tbilisi-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/tbilisi/2020-2029/quest-006-polyphonic-singing.md", "visibility": "internal", "audience": ["narrative", "quest-design", "audio"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown-описание концерта не фиксировало уникальные особенности грузинской полифонии и взаимодействия игрока.", "goal": "Структурировать культурный квест, раскрывающий традицию многоголосия и вовлекающий игрока в мастер-класс.", "essence": "Игрок посещает концерт, изучает историю полифонии и пробует спеть одну из партий хора.", "key_points": ["Грузинское многоголосье включено в список UNESCO.", "Композиции «Чакруло» и «Мравалжамиер» демонстрируют духовную глубину.", "Мастер-класс позволяет пройти базовую тренировку пения."]}, "content": {"sections": [{"id": "concert", "title": "Концертный опыт", "body": "Игрок посещает камерный зал, где мужской хор исполняет «Чакруло», «Мравалжамиер» и застольные песни.\\nАудиогид объясняет распределение голосов и ритмические особенности.\\n", "mechanics_links": [], "assets": []}, {"id": "backstage", "title": "За кулисами", "body": "- Встреча с художественным руководителем ансамбля.\\n- Просмотр архивных записей полифонии и историй о передаче традиций.\\n", "mechanics_links": [], "assets": []}, {"id": "workshop", "title": "Мастер-класс", "body": "Игрок выбирает партию (верхний голос, бас, бурдон) и выполняет мини-игру на удержание тона.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1 500 XP.\\n- Издержки: −20 кредов.\\n- +15 к параметру «Культура».\\n- Ачивка «Ценитель многоголосья».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Чакруло", "definition": "Традиционная грузинская песня, включённая на золотой диск Voyager."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Грузинское многоголосье» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

