INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-baku-2020-2029-gobustan-petroglyphs', 'Баку 2020-2029 — «Петроглифы Гобустана»', 'Игрок отправляется из Баку в Гобустан, изучает музей и древние рисунки, расширяя знания о жизни каменного века.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-baku-2020-2029-gobustan-petroglyphs", "title": "Баку 2020-2029 — «Петроглифы Гобустана»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T01:25:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["baku", "quest", "gobustan"], "topics": ["archaeology", "cultural-heritage"], "related_systems": ["narrative-service", "exploration-service", "heritage-service"], "related_documents": [{"id": "github-issue-906", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/906", "relation": "migrated_to", "migrated_at": "2025-11-23T01:25:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/baku/2020-2029/quest-009-gobustan-petroglyphs.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Археологическая линия Азербайджана требовала квеста о древней истории и UNESCO-объекте.", "goal": "Погрузить игрока в исследование петроглифов Гобустана с образовательными и экспедиционными элементами.", "essence": "Игрок отправляется из Баку в Гобустан, изучает музей и древние рисунки, расширяя знания о жизни каменного века.", "key_points": ["Петроглифы возрастом до 40 000 лет раскрывают охоту, танцы и культовые сцены.", "Музей использует VR-реконструкции для оживления истории.", "Дополнительные активности у грязевых вулканов усиливают уникальность локации."]}, "content": {"sections": [{"id": "journey", "title": "Дорога из Баку", "body": "Игрок выбирает транспорт и готовится к экспедиции. Гид рассказывает о пустынном ландшафте и значении комплекса.\\n", "mechanics_links": ["mechanics/travel/excursion-planning.yaml"], "assets": []}, {"id": "museum", "title": "Музей Гобустана", "body": "Интерактивные экспонаты, VR-сцены и аудиогид показывают быт каменного века. Игрок собирает подсказки к петроглифам.\\n", "mechanics_links": ["mechanics/narrative/interactive-museum.yaml"], "assets": []}, {"id": "rock-art", "title": "Тропа петроглифов", "body": "На скальном массиве игрок сканирует рисунки охотников, животных и танцев. Расшифровка приносит археологические очки.\\n", "mechanics_links": ["mechanics/exploration/landmark-analysis.yaml"], "assets": []}, {"id": "mud-volcano", "title": "Грязевые вулканы", "body": "Побочная активность у булькающих вулканов добавляет ресурсы и фотографии для коллекции.\\n", "mechanics_links": ["mechanics/exploration/environmental-event.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 2 500 XP, характеристика «Археология» +15, доступ к археологическим сюжетам региона.\\n- Ачивка «Археолог Гобустана» и фотокарточки UNESCO.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает археологическую линию Азербайджана, образовательные программы UNESCO и региональные экспедиции.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 906, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Петроглифы Гобустана» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

