-- Quest: quest-dallas-2029-002-cowboys-stadium
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-dallas-2029-002-cowboys-stadium', 'Даллас — стадион Dallas Cowboys', 'Игрок посещает матч Dallas Cowboys, взаимодействует с фанатскими активностями и усиливает влияние спортивной линии Далласа.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-dallas-2029-002-cowboys-stadium", "title": "Даллас — стадион Dallas Cowboys", "document_type": "canon", "category": "narrative", "status": "approved", "version": "0.1.0", "last_updated": "2025-11-11T06:20:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["dallas", "sports", "nfl"], "topics": ["city_storylines", "entertainment"], "related_systems": ["narrative-service", "event-service"], "related_documents": [{"id": "master-timeline", "relation": "references"}, {"id": "quest-system", "relation": "aligns"}, {"id": "github-issue-331", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/331", "relation": "migrated_to", "migrated_at": "2025-11-22T19:45:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/dallas/2020-2029/quest-002-cowboys-stadium.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Текст описывал стадион и матч Cowboys, но не объяснял игровые мотиваторы и связи с социальными системами.", "goal": "Подчеркнуть масштабность AT&T Stadium и вовлечь игрока в спортивную экосистему города с понятными наградами и социальными эффектами.", "essence": "Игрок посещает матч Dallas Cowboys, взаимодействует с фанатскими активностями и усиливает влияние спортивной линии Далласа.", "key_points": ["Стадион служит центром событий и экономических активностей для игрока.", "Внедряются социальные выборы между фанатами и корпоративными гостями.", "Квест открывает доступ к эксклюзивным спортивным контрактам."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Сценарий погружает игрока в атмосферу AT&T Stadium с огромным видеокубом, фан-зоной и фирменными традициями Dallas Cowboys.\\nИгрок изучает инфраструктуру, пробует VIP-лаунж либо фан-сектор и фиксирует эмоции толпы для социальной системы.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "regional_highlights", "title": "Региональные отсылки", "body": "Прозвище «America''s Team», стоимость стадиона в 1,3 миллиарда долларов и знаменитый видеокуб поддерживают узнаваемость бренда.\\nКультура ковбоев и хореография Dallas Cowboys Cheerleaders создают уникальный стиль матча.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы квеста", "body": "1. Прибытие в Арлингтон и обзор стадиона с AR-гидом.\\n2. Выбор входа: фанатский или корпоративный, влияющий на будущие связи.\\n3. Наблюдение за матчем, интерактив с видеокубом и выполнение челленджей по шуму трибун.\\n4. Общение с фанатами и тренерами о традиции «How ''bout them Cowboys!?»\\n5. Финальный выбор — поддержать фан-клуб или подписать VIP-контракт для фракции.\\n", "mechanics_links": ["mechanics/social/social-mechanics-overview.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Квест выдаёт 2000 опыта, снижает валюту на 120 единиц за билет и атрибутику, повышает параметр «Спорт» на 25.\\nИгрок получает ачивку «Ковбой Далласа» и приглашение на будущие NFL-события.\\n", "mechanics_links": [], "assets": []}, {"id": "integration", "title": "Интеграции и последствия", "body": "Event-service регистрирует матч как сезонное событие, narrative-service связывает спортивную линию с другими городскими квестами,\\nа социальные механики учитывают сделанный выбор фанатского или корпоративного пути.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 331, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_team", "changes": "Конвертирован квест «Стадион Dallas Cowboys» в структурированный YAML с интеграцией в спортивную линию."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

