INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-kiev-2020-2029-maidan-legacy', 'Киев 2020-2029 — «Наследие Майдана»', 'Игрок посещает Майдан, слышит свидетельства и решает, как поддержать свободу, стабильность или компромисс.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-kiev-2020-2029-maidan-legacy", "title": "Киев 2020-2029 — «Наследие Майдана»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["kiev", "quest", "maidan"], "topics": ["civic-movement", "political-choice"], "related_systems": ["narrative-service", "reputation-service", "history-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/kiev/2020-2029/quest-001-maidan-legacy.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Политическая линия Киева требовала квеста о наследии Майдана и влиянии протестов.", "goal": "Осветить истории участников, выбор текущей позиции и последствия для городской политики.", "essence": "Игрок посещает Майдан, слышит свидетельства и решает, как поддержать свободу, стабильность или компромисс.", "key_points": ["Мемориал Небесной Сотни формирует эмоциональный центр.", "Диалоги с ветеранами раскрывают разные взгляды на революции.", "Выбор игрока меняет репутацию у гражданских и политических фракций."]}, "content": {"sections": [{"id": "memorial", "title": "Мемориал Майдана", "body": "Игрок возлагает цветы и изучает цифровой архив, посвящённый героям. Открываются аудиоистории очевидцев.\\n", "mechanics_links": ["mechanics/history/memorial-interaction.yaml"], "assets": []}, {"id": "testimonies", "title": "Свидетельства", "body": "Встречи с ветеранами 2004 и 2014 годов. Игрок выбирает вопросы, узнаёт о цене свободы и получает задания по поддержке\\nсообществ.\\n", "mechanics_links": ["mechanics/narrative/dialogue-web.yaml"], "assets": []}, {"id": "decision", "title": "Новый выбор", "body": "Появляется актуальная дилемма: поддержать протест, усилить стабильность или найти компромисс. Решение меняет политическую карту\\nКиева.\\n", "mechanics_links": ["mechanics/narrative/player-stance.yaml"], "assets": []}, {"id": "consequences", "title": "Последствия", "body": "Город реагирует на выбор игрока: открываются протестные марши, реформы или переговорные миссии. Репутация «Свобода» обновляется.\\n", "mechanics_links": ["mechanics/world/state-shift.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 2 000 XP, репутация «Свобода» +10, влияние на политические ветки Киева.\\n- Доступ к сюжетам революционных городов и ачивка «Голос Майдана».\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест соединяет революционные сюжеты Киева, Санкт-Петербурга и глобальные линии свободы.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 891, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Наследие Майдана» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

