INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-kiev-2020-2029-lavra-pilgrimage', 'Киев 2020-2029 — «Паломничество в Лавру»', 'Игрок проходит через Святые врата, исследует храмы и пещеры, получая духовные баффы.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-kiev-2020-2029-lavra-pilgrimage", "title": "Киев 2020-2029 — «Паломничество в Лавру»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["kiev", "quest", "lavra"], "topics": ["spiritual-journey", "heritage"], "related_systems": ["narrative-service", "ritual-service", "wellbeing-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/kiev/2020-2029/quest-002-lavra-pilgrimage.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Духовная линия Киева требовала квеста о Киево-Печерской лавре и паломническом опыте.", "goal": "Передать атмосферу древнего монастыря, посещение пещер и получение благословения.", "essence": "Игрок проходит через Святые врата, исследует храмы и пещеры, получая духовные баффы.", "key_points": ["Лабиринт пещер с мощами подчёркивает древность места.", "Золотые купола и колокольный звон создают визуально-звуковую сцену.", "Благословение усиливает защиту и открывает новые духовные диалоги."]}, "content": {"sections": [{"id": "entrance", "title": "Святые врата", "body": "Игрок поднимается на холм и входит в лавру. Гид рассказывает историю основания 1051 года и роль монастыря.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "cathedral", "title": "Успенский собор", "body": "Внутри собора игрок наблюдает росписи и службы. Возможна мини-игра с иконографией и выбор пожертвования.\\n", "mechanics_links": ["mechanics/rituals/sanctuary-interaction.yaml"], "assets": []}, {"id": "caves", "title": "Пещеры", "body": "Темные коридоры с мощами святых требуют свечи. Игрок использует навигацию, чтобы найти особые святыни и получить баффы.\\n", "mechanics_links": ["mechanics/exploration/dungeon-navigation.yaml"], "assets": []}, {"id": "blessing", "title": "Благословение", "body": "После молитвы монахи дают благословение, повышающее защиту. Игрок получает доступ к духовным поручениям.\\n", "mechanics_links": ["mechanics/rituals/blessing-system.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1 000 XP, расходы 10 еддис на свечи, бафф «Благословение» +10% к защите на 48 часов.\\n- Ачивка «Паломник Киева» и доступ к духовным событиям.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает духовную линию Киева, религиозные фестивали и межрегиональные паломничества.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 892, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Паломничество в Лавру» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

