INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-spb-2093-bronze-horseman-farewell', 'Санкт-Петербург 2078-2093 — Прощание с Медным всадником', 'Игрок беседует с ИИ-Медным всадником, получает итоговый монолог и благословение на новый сезон.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-spb-2093-bronze-horseman-farewell", "title": "Санкт-Петербург 2078-2093 — Прощание с Медным всадником", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "legacy", "ai"], "topics": ["narrative_closure", "legacy_system"], "related_systems": ["narrative-service", "progression-service"], "related_documents": [{"id": "quest-spb-2040-bronze-horseman", "relation": "follows"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2078-2093/quest-046-bronze-horseman-farewell.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Финальная встреча с ИИ-аватаром Петра I оставалась в Markdown и не фиксировала механику благословения.", "goal": "Описать диалог и награду перед перезапуском лиги, связывая квест с legacy-системой города.", "essence": "Игрок беседует с ИИ-Медным всадником, получает итоговый монолог и благословение на новый сезон.", "key_points": ["ИИ Пётр I резюмирует историю Петербурга и даёт выбор фраз-ответов.", "Благословение «Город будет жить вечно» добавляет бонус к стартовым параметрам.", "Квест закрывает линию Медного всадника."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "«Прощание с Медным всадником» — последняя встреча с легендарным символом города. Сенатская площадь замирает, когда\\nИИ-статуя оживает, чтобы попрощаться с игроком перед новой лигой.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Прибыть к памятнику на Сенатской площади в назначенный час.\\n2. Активировать ИИ-Петра и выслушать историю города.\\n3. Пройти диалоговые ветки: воспоминания, уроки, предупреждения.\\n4. Принять благословение и выбрать посыл для новой лиги.\\n5. Зафиксировать финальные слова в хронике.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Бронзовый конь на дыбах, змея у копыт, холодный ветер Невы и голографические проекции прошлых веков. Голос Петра звучит\\nвластно и благословляет «Город Петра».\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 5 000 XP.\\n- Благословение Петра: +10 % к стартовым параметрам следующей лиги.\\n- Запись монолога в личном архиве игрока.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Диалоговое взаимодействие с ИИ, выбор эмоциональных ответов и включение legacy-бонусов.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Замыкает линию, начатую в квесте «Медный всадник», и ведёт к финальному событию «Вечный город».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Прощание с Медным всадником» в формат знаний Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

