INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-kiev-2020-2029-dnieper-crossing', 'Киев 2020-2029 — «Переправа через Днепр»', 'Игрок выбирает способ переправы, сталкивается с контрабандой и решает, как поступить.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-kiev-2020-2029-dnieper-crossing", "title": "Киев 2020-2029 — «Переправа через Днепр»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["kiev", "quest", "dnepr"], "topics": ["urban-navigation", "moral-choice"], "related_systems": ["narrative-service", "transport-service", "reputation-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/kiev/2020-2029/quest-003-dnieper-crossing.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Транспортная линия Киева требовала квеста о переправе через Днепр и выборе маршрута.", "goal": "Показать инфраструктуру города, встречи на воде и моральную дилемму с контрабандистами.", "essence": "Игрок выбирает способ переправы, сталкивается с контрабандой и решает, как поступить.", "key_points": ["Мосты Патона, метро и водный путь дают разные преимущества.", "Контрабандисты предлагают сделку, испытание или возможность ареста.", "Результат влияет на репутацию и доступ к транспортным бонусам."]}, "content": {"sections": [{"id": "planning", "title": "Планирование переправы", "body": "Игрок анализирует варианты: автомобильный мост, поезд метро или аренда лодки. Выбор влияет на время и встречи.\\n", "mechanics_links": ["mechanics/transport/route-selection.yaml"], "assets": []}, {"id": "crossing", "title": "Путь через реку", "body": "Модуль прохода учитывает ширину Днепра, погодные условия и визуальные виды. На воде могут появиться острова и скрытые точки.\\n", "mechanics_links": ["mechanics/navigation/river-crossing.yaml"], "assets": []}, {"id": "encounter", "title": "Контрабандисты", "body": "Встреча на середине пути ставит моральную дилемму: помочь ввезти груз, сообщить властям или уйти. Решение меняет репутацию.\\n", "mechanics_links": ["mechanics/narrative/moral-choice.yaml"], "assets": []}, {"id": "outcome", "title": "Исход", "body": "В зависимости от выбора игрок получает награду, штраф или новые контакты. Город реагирует изменением патрулей и цен.\\n", "mechanics_links": ["mechanics/world/state-shift.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 800 XP, 500 еддис, знание мостов и транспортных схем.\\n- Бонусы к навигации и доступ к транспортным цепочкам.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает транспортную линию Киева, экономические сценарии и контрабандные арки по Днепру.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 893, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Переправа через Днепр» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

