INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-kiev-2020-2029-borsch-quest', 'Киев 2020-2029 — «Квест борща»', 'Игрок собирает рекомендации, посещает три заведения и выбирает победителя, провоцируя культурный спор.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-kiev-2020-2029-borsch-quest", "title": "Киев 2020-2029 — «Квест борща»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["kiev", "quest", "borsch"], "topics": ["culinary-experience", "cultural-identity"], "related_systems": ["narrative-service", "wellbeing-service", "liveops-events-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/kiev/2020-2029/quest-004-borsch-quest.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Кулинарная линия Киева нуждалась в интерактивном споре о лучшем борще.", "goal": "Организовать дегустацию разных рецептов и вовлечь игрока в дискуссию о национальном блюде.", "essence": "Игрок собирает рекомендации, посещает три заведения и выбирает победителя, провоцируя культурный спор.", "key_points": ["Варианты борща различаются ингредиентами и подачей.", "Сало, сметана и пампушки усиливают национальные ассоциации.", "Квест легко превращается в LiveOps гастрономические события."]}, "content": {"sections": [{"id": "survey", "title": "Опрос местных", "body": "Игрок общается с NPC, собирает список обязательных заведений и узнаёт секреты рецептов.\\n", "mechanics_links": ["mechanics/social/dialogue-practice.yaml"], "assets": []}, {"id": "tasting", "title": "Дегустационный тур", "body": "Три остановки: красный свекольный, зелёный щавелевый и холодный летний борщ. Игрок фиксирует вкусы и эффекты wellbeing.\\n", "mechanics_links": ["mechanics/food/tasting-events.yaml"], "assets": []}, {"id": "verdict", "title": "Выбор победителя", "body": "Игрок объявляет лучший рецепт, получает реакцию от гостей и вызывает дискуссию с оппонентами.\\n", "mechanics_links": ["mechanics/narrative/branching-decisions.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 600 XP, -30 валюты, бафф +25% HP на 8 часов, ачивка «Знаток борща».\\n- Улучшение репутации в кулинарных клубах Киева.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает кулинарные сюжеты региона и международные гастрономические конкурсы.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 894, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Квест борща» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

