INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-spb-2093-last-waltz', 'Санкт-Петербург 2078-2093 — Последний вальс', 'Игрок надевает бальный наряд, находит партнёра и исполняет последний вальс под свечение люстр.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-spb-2093-last-waltz", "title": "Санкт-Петербург 2078-2093 — Последний вальс", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "ballroom", "farewell"], "topics": ["social_event", "romance"], "related_systems": ["narrative-service", "social-service"], "related_documents": [{"id": "quest-spb-2030-imperial-ball", "relation": "mirrors"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2078-2093/quest-048-last-waltz.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Последний балл империи хранился в Markdown и не фиксировал механику танцевальной мини-игры.", "goal": "Описать финальный вальс в Николаевском зале, где игрок прощается с сезоном.", "essence": "Игрок надевает бальный наряд, находит партнёра и исполняет последний вальс под свечение люстр.", "key_points": ["Присутствуют элементы танцевальной мини-игры и проверки синхронизации.", "Атмосфера имперского бала поддерживается оркестром и интерьером зала.", "Награда усиливает связи с партнёром и романтический рейтинг."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "«Последний вальс» проходит в Николаевском зале Зимнего дворца. Это прощальный танец сезона, где игроки под музыку\\nклассического оркестра вспоминают прошедшие события.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить приглашение и подготовить бальный наряд.\\n2. Найти партнёра, синхронизировать шаги и пози.\\n3. Исполнить танцевальную мини-игру под оркестр.\\n4. Отметить финальный поклон и обменяться пожеланиями перед новой лигой.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Николаевский зал площадью 1 103 м², хрустальные люстры, зеркальные стены и оркестр в золотых мундирах усиливают\\nторжественность. На паркете слышен скрип обуви и шёпот «Последний вальс империи».\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 500 XP.\\n- Романс +20 с партнёром.\\n- Эмоциональный бафф «Прощальный танец».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Танцевальная мини-игра с проверками на синхронность, социальные взаимодействия и эмоциональные реакции NPC.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Продолжает традицию имперских балов и завершает культурную линию перед «Вечным городом».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Последний вальс» в формат знаний Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

