INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tashkent-samsa-quest', 'Ташкент 2020-2029 — «Квест самсы»', 'Игрок собирает рекомендации местных, пробует разные самсы и участвует в мастер-классе по лепке.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tashkent-samsa-quest", "title": "Ташкент 2020-2029 — «Квест самсы»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["tashkent", "cuisine", "samsa"], "topics": ["culinary", "exploration"], "related_systems": ["narrative-service", "culinary-system", "reputation-system"], "related_documents": [{"id": "canon-region-cis-tashkent-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/tashkent/2020-2029/quest-006-samsa-quest.md", "visibility": "internal", "audience": ["narrative", "quest-design", "live_ops"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Скетч не структурировал гастрономический тур и мастер-класс по самсе.", "goal": "Создать кулинарный квест с поиском лучшей самсы и обучением работе с тандыром.", "essence": "Игрок собирает рекомендации местных, пробует разные самсы и участвует в мастер-классе по лепке.", "key_points": ["Тандыр — ключевой элемент и источник баффов.", "Разные начинки раскрывают богатство узбекской кухни.", "Финальное голосование определяет, кто получит знак «Лучшая самса»."]}, "content": {"sections": [{"id": "scouting", "title": "Поиск лучших точек", "body": "Игрок общается с местными жителями и собирает советы, какие лавки посетить.\\n", "mechanics_links": [], "assets": []}, {"id": "tasting", "title": "Дегустация", "body": "- Самса с бараниной.\\n- Самса с тыквой.\\n- Самса с картофелем.\\nМини-игра «оценка сочности» определяет бонусы.\\n", "mechanics_links": [], "assets": []}, {"id": "workshop", "title": "Мастер-класс", "body": "Игрок пробует лепить самсу и прикрепляет её к стенке тандыра, контролируя температуру.\\n", "mechanics_links": [], "assets": []}, {"id": "verdict", "title": "Выбор победителя", "body": "Голосование за любимую точку с возможностью дать рекомендацию в городском канале.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 600 XP.\\n- Издержки: −15 кредов.\\n- Бафф «+15% HP на 6 часов».\\n- Ачивка «Мастер самсы».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Тандыр", "definition": "Глиняная печь, используемая в Средней Азии для выпечки хлеба и самсы."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Квест самсы» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

