-- Quest: canon-quest-vladivostok-2020-2029-sea-of-japan
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-vladivostok-2020-2029-sea-of-japan', 'Владивосток 2020-2029 — «Японское море»', 'Игрок проходит по набережной, знакомится с флотом и встречает закат над Японским морем.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-vladivostok-2020-2029-sea-of-japan", "title": "Владивосток 2020-2029 — «Японское море»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vladivostok", "quest", "seaside"], "topics": ["city_quests", "lifestyle"], "related_systems": ["narrative-service", "world-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/vladivostok/2020-2029/quest-004-sea-of-japan.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Городская линия Владивостока нуждается в атмосферной прогулке, подчёркивающей морское наследие.", "goal": "Показать Японское море и набережную Цесаревича как ключевую зону отдыха и визитку города.", "essence": "Игрок проходит по набережной, знакомится с флотом и встречает закат над Японским морем.", "key_points": ["Проход по набережной связывает игрока с историей Тихоокеанского флота.", "Расслабляющая механика раскрывает жизненный уклад берегового города.", "Закат и морской воздух становятся эмоциональной кульминацией ранней линии Владивостока."]}, "content": {"sections": [{"id": "overview", "title": "Контекст", "body": "После вступительных заданий игрок получает приглашение от гидов Владивостока прогуляться по набережной Цесаревича\\nи почувствовать контакт с океаном, который формирует характер города.\\n", "mechanics_links": [], "assets": []}, {"id": "flow", "title": "Этапы", "body": "1. Старт у входа на набережную и знакомство с историей Тихоокеанского флота.\\n2. Осмотр пришвартованных кораблей и посещение мемориала морякам.\\n3. Фиксация атмосферных звуков и сбор артефактов (ракушки, фотографии).\\n4. Таймлапс-закат над Японским морем с возможностью meditation-сессии.\\n5. Запись впечатлений в дневник, влияющих на социальные взаимодействия.\\n", "mechanics_links": [], "assets": []}, {"id": "ambience", "title": "Атмосфера", "body": "Уровень шума моря, крики чаек и голограммы об истории флота создают эффект присутствия.\\nВечером включается мягкая подсветка, подчёркивающая геометрию бухты.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "Игрок получает 600 XP, релакс-бафф «Морской бриз» (+10 к восстановлению) и ачивку «Морской Прогулочник».\\nДенежные траты отсутствуют.\\n", "mechanics_links": [], "assets": []}, {"id": "relations", "title": "Связи", "body": "Квест поддерживает морскую линию Владивостока и предшествует заданиям о камчатском крабе и острове Русском.\\nИспользуется в онбординге туристических механик.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Японское море» в формат knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

