INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tashkent-non-bread', 'Ташкент 2020-2029 — «Лепёшка нон»', 'Игрок учится замешивать тесто, наносит узор чекичем и выпекает нон, соблюдая ритуалы уважения к хлебу.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tashkent-non-bread", "title": "Ташкент 2020-2029 — «Лепёшка нон»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["tashkent", "bread", "tradition"], "topics": ["culinary", "workshop"], "related_systems": ["narrative-service", "culinary-system", "reputation-system"], "related_documents": [{"id": "canon-region-cis-tashkent-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/tashkent/2020-2029/quest-009-non-bread.md", "visibility": "internal", "audience": ["narrative", "quest-design", "live_ops"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест не описывал практическую часть выпечки нона и символику хлеба.", "goal": "Сделать мастер-класс по выпечке лепёшки с тандыром и культурными традициями.", "essence": "Игрок учится замешивать тесто, наносит узор чекичем и выпекает нон, соблюдая ритуалы уважения к хлебу.", "key_points": ["Тандыр требует выдержки температуры и правильного прилепления.", "Узоры обозначают регион и семью пекаря.", "Нарушение традиций (например, резка ножом) снижает репутацию."]}, "content": {"sections": [{"id": "bakery", "title": "Знакомство с пекарней", "body": "Игрок посещает семейную пекарню и знакомится с мастером тандыра.\\n", "mechanics_links": [], "assets": []}, {"id": "dough", "title": "Замес теста", "body": "Мини-игра по замесу, правильной текстуре и добавлению кунжута.\\n", "mechanics_links": [], "assets": []}, {"id": "stamping", "title": "Узоры чекичем", "body": "Игрок наносит орнамент, выбирая символику района и семьи.\\n", "mechanics_links": [], "assets": []}, {"id": "baking", "title": "Выпечка в тандыре", "body": "Контроль температуры и времени, чтобы получить золотистую корочку.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 800 XP.\\n- +10 к навыку «Выпечка».\\n- Ачивка «Мастер нона».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Чекич", "definition": "Деревянная печать с шипами, используемая для нанесения узора на лепёшку."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Лепёшка нон» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

