INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-kiev-2020-2029-khreschatyk-walk', 'Киев 2020-2029 — «Прогулка по Крещатику»', 'Игрок выбирает время прогулки, взаимодействует с жителями и собирает эмоции улицы для культурных сюжетов Киева.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "arrive_khreschatyk", "text": "Прибыть на Крещатик в выходной день", "type": "interact", "target": "khreschatyk_entrance", "count": 1, "optional": false}, {"id": "explore_street", "text": "Исследовать культурную жизнь улицы", "type": "interact", "target": "street_culture", "count": 1, "optional": false}, {"id": "interact_locals", "text": "Пообщаться с местными жителями", "type": "interact", "target": "local_residents", "count": 3, "optional": false}, {"id": "document_emotions", "text": "Задокументировать эмоции улицы", "type": "interact", "target": "street_emotions", "count": 1, "optional": false}]'::jsonb, '{"experience": 500, "currency": -500, "items": [], "reputation": {}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-kiev-2020-2029-khreschatyk-walk", "title": "Киев 2020-2029 — «Прогулка по Крещатику»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["kiev", "quest", "khreschatyk"], "topics": ["urban-lifestyle", "social-experience"], "related_systems": ["narrative-service", "liveops-events-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/kiev/2020-2029/quest-009-khreschatyk-walk.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Первичная заметка описывала прогулку по Крещатику без геймплейных акцентов и динамики выходного дня.", "goal": "Создать slice-of-life квест с атмосферой выходного Крещатика, уличными артистами и кафе под каштанами.", "essence": "Игрок выбирает время прогулки, взаимодействует с жителями и собирает эмоции улицы для культурных сюжетов Киева.", "key_points": ["Крещатик становится пешеходным по выходным, жители заполняют улицу.", "Символические каштаны и уличные музыканты создают идентичность города.", "Квест снижает стресс и открывает социальные сценарии знакомств."]}, "quest_definition": {"quest_type": "side", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "arrive_khreschatyk", "text": "Прибыть на Крещатик в выходной день", "type": "interact", "target": "khreschatyk_entrance", "count": 1, "optional": false}, {"id": "explore_street", "text": "Исследовать культурную жизнь улицы", "type": "interact", "target": "street_culture", "count": 1, "optional": false}, {"id": "interact_locals", "text": "Пообщаться с местными жителями", "type": "interact", "target": "local_residents", "count": 3, "optional": false}, {"id": "document_emotions", "text": "Задокументировать эмоции улицы", "type": "interact", "target": "street_emotions", "count": 1, "optional": false}], "rewards": {"experience": 500, "currency": -500, "items": [], "reputation": {}}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Игрок прибывает на Крещатик в выходной день, когда движение перекрыто. Задача — исследовать культурную жизнь улицы,\\nпообщаться с NPC и задокументировать городские практики.\\n", "mechanics_links": [], "assets": []}, {"id": "promenade", "title": "Маршрут", "body": "Прогулка начинается на Майдане Незалежности и заканчивается на Европейской площади. Игрок отмечает выступления музыкантов,\\nфонтаны, уличные художники и фотографирует каштаны в цвету.\\n", "mechanics_links": [], "assets": []}, {"id": "social", "title": "Социальные взаимодействия", "body": "По пути можно познакомиться с местными жителями, выполнить мини-задания по сбору историй и организовать импровизированные встречи,\\nвлияющие на репутацию в городской среде.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 500 XP, снижение стресса персонажа на 30%.\\n- Финансовые траты 500 единиц валюты на кафе и сувениры.\\n- Ачивка «Прогулка киевлянина» и доступ к социальным событиям линии Киева.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест поддерживает туристическую арку Киева и связывает её с культурными ивентами, гастрономическими сюжетами и социальными\\nмиссиями города.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 862, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Прогулка по Крещатику» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

