INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-kiev-2020-2029-cossack-heritage', 'Киев 2020-2029 — «Казацкое наследие»', 'Игрок отправляется на Хортицу, проходит испытания верховой езды, оружия и гопака, а затем приносит обет братству.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-kiev-2020-2029-cossack-heritage", "title": "Киев 2020-2029 — «Казацкое наследие»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["kiev", "quest", "cossacks"], "topics": ["warrior-traditions", "faction-initiation"], "related_systems": ["narrative-service", "combat-service", "reputation-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/kiev/2020-2029/quest-010-cossack-heritage.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Военная линия Киева требовала квеста о возрождении казацкого братства и связанном с ним отборе.", "goal": "Организовать вступление игрока в «Новых казаков», подчеркнув связь с историей Запорожской Сечи.", "essence": "Игрок отправляется на Хортицу, проходит испытания верховой езды, оружия и гопака, а затем приносит обет братству.", "key_points": ["Испытания охватывают ключевые навыки казаков: конный бой, владение саблей, стрельбу из лука и гопак.", "Церемония проходит на исторической Хортице под руководство атамана.", "Успешное посвящение открывает доступ к гильдии, кавалерийским бонусам и уникальному вооружению."]}, "content": {"sections": [{"id": "recon", "title": "Разведка братства", "body": "Игрок получает наводку на лагерь «Новых казаков», расположенный на острове Хортица, и проходит путь через степные\\nтропы. Вступительный совет оценивает происхождение, честь и готовность защищать Украину.\\n", "mechanics_links": ["mechanics/travel/expedition-planning.yaml"], "assets": []}, {"id": "trials", "title": "Испытания воина", "body": "Серия испытаний проверяет верховую езду, владение саблей, стрельбу из составного лука и ритмику гопака. Результаты\\nфиксируются старшинами, а ошибки снижают доверие братства.\\n", "mechanics_links": ["mechanics/combat/weapon-challenges.yaml", "mechanics/social/performance-check.yaml"], "assets": []}, {"id": "oath", "title": "Казачья трапеза и обет", "body": "После испытаний игрок участвует в традиционном застолье с горилкой и слушает легенды о Запорожской Сечи.\\nЦеремония завершаетcя принесением обета, вручением шароваров и символической сабли.\\n", "mechanics_links": ["mechanics/narrative/oath-ceremony.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 4 000 XP, доступ к гильдии «Новые казаки».\\n- Бонус кавалерии +15%, уникальные варианты сабель и конской брони.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает военную линию Киева с украинскими историческими квестами и открывает цепочки заданий братства в\\nдругих регионах.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 863, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Казацкое наследие» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

