INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tashkent-cotton-fields', 'Ташкент 2020-2029 — «Хлопковые поля»', 'Игрок работает на полях, узнаёт историю «белого золота» и принимает решение о будущем отрасли.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tashkent-cotton-fields", "title": "Ташкент 2020-2029 — «Хлопковые поля»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["uzbekistan", "cotton", "ecology"], "topics": ["labor-history", "environmental-choice"], "related_systems": ["narrative-service", "reputation-system", "economy-system"], "related_documents": [{"id": "canon-region-cis-uzbekistan-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/tashkent/2020-2029/quest-005-cotton-fields.md", "visibility": "internal", "audience": ["narrative", "quest-design", "environment"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown не раскрывал выбор между экономикой хлопка и экологической катастрофой Арала.", "goal": "Структурировать фракционный квест о сборе хлопка и последствиях ирригации.", "essence": "Игрок работает на полях, узнаёт историю «белого золота» и принимает решение о будущем отрасли.", "key_points": ["Сбор хлопка вручную под палящим солнцем тестирует выносливость.", "Диалоги рассказывают про советскую монокультуру и высыхание Арала.", "Финальный выбор влияет на экологическую линию Узбекистана."]}, "content": {"sections": [{"id": "labor", "title": "Поле и труд", "body": "Игрок изучает технику сбора хлопка, соревнуется в скорости и получает бонус к навыку земледелия.\\nПараметр «Усталость» растёт из-за жары.\\n", "mechanics_links": [], "assets": []}, {"id": "history", "title": "История белого золота", "body": "Наставник рассказывает о советских планах, коррупции и последствиях на Аральское море.\\nИгрок фиксирует данные для экологического отчёта.\\n", "mechanics_links": [], "assets": []}, {"id": "decision", "title": "Решение игрока", "body": "- Продолжать: сохранить доход, но ухудшить экологию.\\n- Бросить: потерять поддержку фермеров.\\n- Реформа: инвестировать в устойчивые технологии и капельный полив.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 2 000 XP, 50 кредов, +10 к навыку «Сельское хозяйство».\\n- Эмоциональный момент «Белое золото» и изменение репутации в зависимости от выбора.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Белое золото", "definition": "Прозвище хлопка в Узбекистане, символизирующее экономическое значение и зависимость страны."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Хлопковые поля» в knowledge-entry и описание экологического выбора."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

