INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tbilisi-khachapuri-tour', 'Тбилиси 2020-2029 — «Квест хачапури»', 'Игрок посещает четыре заведения, пробует разные виды хачапури и выбирает любимый рецепт.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tbilisi-khachapuri-tour", "title": "Тбилиси 2020-2029 — «Квест хачапури»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["tbilisi", "cuisine", "khachapuri"], "topics": ["culinary", "exploration"], "related_systems": ["narrative-service", "culinary-system", "reputation-system"], "related_documents": [{"id": "canon-region-cis-tbilisi-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/tbilisi/2020-2029/quest-005-khachapuri-quest.md", "visibility": "internal", "audience": ["narrative", "quest-design", "live_ops"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Кулинарный маршрут по хачапури не фиксировал разнообразие видов и механики дегустации.", "goal": "Оформить квест как гастрономический тур по лучшим местам Тбилиси с обучением подаче аджарского хачапури.", "essence": "Игрок посещает четыре заведения, пробует разные виды хачапури и выбирает любимый рецепт.", "key_points": ["Аджарский, имеретинский, мегрельский и пеновани представлены как отдельные станции.", "Взаимодействие с шефами раскрывает секреты теста и сыра сулугуни.", "Финальное голосование фиксирует предпочтение игрока и открывает новые рецепты."]}, "content": {"sections": [{"id": "route", "title": "Построение маршрута", "body": "Игрок получает карту кулинарного тура и бронь в топовых хинкальных.\\nПланирование времени влияет на свежесть блюд и бонусы.\\n", "mechanics_links": [], "assets": []}, {"id": "tasting", "title": "Дегустация", "body": "- Аджарский: лодочка с яйцом, мини-игра «правильно перемешай масло».\\n- Имеретинский: оценка текстуры и распознавание специй.\\n- Мегрельский: двойной сыр, проверка на лучший сырный тяг.\\n- Пеновани: слоёный вариант, оценка хрустящей корочки.\\n", "mechanics_links": [], "assets": []}, {"id": "verdict", "title": "Выбор фаворита", "body": "Игрок отдает голос за понравившийся вид и делится отзывом в гастрономическом канале города.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 600 XP.\\n- Издержки: −25 кредов.\\n- Бафф «+20% HP на 8 часов».\\n- Ачивка «Мастер хачапури».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Сулугуни", "definition": "Традиционный грузинский рассольный сыр, основной ингредиент большинства видов хачапури."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Квест хачапури» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

