INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-minsk-2020-2029-victory-square', 'Минск 2020-2029 — «Площадь Победы»', 'Игрок проходит по обелиску, спускается в зал Славы и взаимодействует с почётным караулом.', 'side', 2, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "lay_flowers", "text": "Подойти к вечному огню, участвовать в церемонии с почётным караулом и возложить цветы по протоколу", "type": "interact", "target": "minsk_victory_square_eternal_flame", "count": 1, "optional": false}, {"id": "explore_hall_of_glory", "text": "Спуститься в подземный зал Славы, изучить мозаики и архивные материалы о войне, записать рассказы ветеранов", "type": "investigate", "target": "minsk_victory_square_hall_of_glory", "count": 1, "optional": false}, {"id": "observe_moment_of_silence", "text": "Принять участие в минуте молчания, зафиксировать эмоцию «Память» и укрепить репутацию среди ветеранов", "type": "interact", "target": "minsk_victory_square_silence", "count": 1, "optional": false}]'::jsonb, '{"experience": 800, "currency": 0, "items": [], "reputation": {"minsk_veterans": 10}, "flags": ["people_memory_achievement"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-minsk-2020-2029-victory-square", "title": "Минск 2020-2029 — «Площадь Победы»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["minsk", "quest", "victory-square"], "topics": ["war-memory", "memorial-rituals"], "related_systems": ["narrative-service", "history-service", "reputation-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/minsk/2020-2029/quest-004-victory-square.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Мемориальная линия Минска требовала квеста о площади Победы, вечном огне и подземном зале Славы.", "goal": "Организовать церемонию памяти с возложением цветов, изучением мозаик и минутой молчания.", "essence": "Игрок проходит по обелиску, спускается в зал Славы и взаимодействует с почётным караулом.", "key_points": ["Обелиск-меч высотой 38 метров символизирует победу во WWII.", "Беларусь потеряла четверть населения, память об этом поддерживает вечный огонь.", "Зал Славы содержит мозаики и имена героев."]}, "quest_definition": {"quest_type": "side", "level_min": 2, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "lay_flowers", "text": "Подойти к вечному огню, участвовать в церемонии с почётным караулом и возложить цветы по протоколу", "type": "interact", "target": "minsk_victory_square_eternal_flame", "count": 1, "optional": false}, {"id": "explore_hall_of_glory", "text": "Спуститься в подземный зал Славы, изучить мозаики и архивные материалы о войне, записать рассказы ветеранов", "type": "investigate", "target": "minsk_victory_square_hall_of_glory", "count": 1, "optional": false}, {"id": "observe_moment_of_silence", "text": "Принять участие в минуте молчания, зафиксировать эмоцию «Память» и укрепить репутацию среди ветеранов", "type": "interact", "target": "minsk_victory_square_silence", "count": 1, "optional": false}], "rewards": {"experience": 800, "currency": 0, "items": [], "reputation": {"minsk_veterans": 10}, "flags": ["people_memory_achievement"]}, "branches": []}, "content": {"sections": [{"id": "arrival", "title": "Возложение цветов", "body": "Игрок подходит к вечному огню, участвует в церемонии с почётным караулом и возлагает цветы по протоколу.\\n", "mechanics_links": ["mechanics/history/memorial-interaction.yaml"], "assets": []}, {"id": "hall-of-glory", "title": "Подземный зал славы", "body": "Спуск в зал открывает мозаики и архивные материалы о войне. Игрок сканирует фрески и записывает рассказы ветеранов.\\n", "mechanics_links": ["mechanics/narrative/testimony-sequence.yaml"], "assets": []}, {"id": "reflection", "title": "Минутa молчания", "body": "После экскурсии проводится минута молчания. Игрок фиксирует эмоцию «Память» и укрепляет репутацию среди ветеранов.\\n", "mechanics_links": ["mechanics/emotions/memorial-silence.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 800 XP, эмоция «Почтение героям».\\n- Ачивка «Память народа» и доступ к мемориальным событиям Минска.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает военные мемориалы Минска с аналогичными линиями Киева и Санкт-Петербурга, открывая совместные\\nцеремонии LiveOps.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 867, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики военной памяти и мемориальных ритуалов."}, {"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Площадь Победы» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXC