INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tbilisi-caucasus-mountains', 'Тбилиси 2020-2029 — «Кавказские горы»', 'Игрок преодолевает подъём к церкви Гергети у подножия Казбека, слушая легенды и встречая местных гидов.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tbilisi-caucasus-mountains", "title": "Тбилиси 2020-2029 — «Кавказские горы»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["tbilisi", "caucasus", "trekking"], "topics": ["adventure", "heritage"], "related_systems": ["narrative-service", "exploration-system", "reputation-system"], "related_documents": [{"id": "canon-region-cis-tbilisi-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/tbilisi/2020-2029/quest-007-caucasus-mountains.md", "visibility": "internal", "audience": ["narrative", "quest-design", "level-design"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Базовое описание треккинга к Казбеку не фиксировало трудности маршрута и символику Гергетской церкви.", "goal": "Структурировать приключение на Военно-Грузинской дороге с акцентом на духовную и природную составляющие.", "essence": "Игрок преодолевает подъём к церкви Гергети у подножия Казбека, слушая легенды и встречая местных гидов.", "key_points": ["Маршрут включает поездку по живописной дороге и пеший подъём 3 часа вверх.", "Гергетская церковь становится точкой духовного переживания и фотоквеста.", "Легенда о Прометее добавляет мифологический пласт."]}, "content": {"sections": [{"id": "road", "title": "Военно-Грузинская дорога", "body": "Игрок отправляется из Тбилиси, встречает торговцев и гидов, собирает провиант.\\nПо пути активируются точки интереса с историческими справками.\\n", "mechanics_links": [], "assets": []}, {"id": "stepantsminda", "title": "Подготовка в Степанцминде", "body": "- Проверка снаряжения и получение благословения местного священника.\\n- Возможность нанять коня или выбрать пеший подъём.\\n", "mechanics_links": [], "assets": []}, {"id": "ascent", "title": "Подъём к Гергети", "body": "Мини-игра на выносливость и ориентирование, влияние погоды и разреженного воздуха.\\n", "mechanics_links": [], "assets": []}, {"id": "summit", "title": "Плато у церкви", "body": "Игрок фотографирует церковь на фоне Казбека и слушает легенду о Прометее.\\nРешение: задокументировать святилище или оставить легенду в тайне.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 3 000 XP.\\n- +20 к параметру «Природа».\\n- Ачивка «Покоритель Кавказа».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Гергетская церковь", "definition": "Троицкая церковь XIV века, расположенная на высоте 2170 м у подножия Казбека."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Кавказские горы» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branche