INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tbilisi-old-town-walk', 'Тбилиси 2020-2029 — «Прогулка по Старому Тбилиси»', 'Игрок исследует Старый Тбилиси, посещает Абанотубани, крепость Нарикала и статую «Мать-Грузия», завершая прогулку панорамой.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tbilisi-old-town-walk", "title": "Тбилиси 2020-2029 — «Прогулка по Старому Тбилиси»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["tbilisi", "old-town", "tourism"], "topics": ["cultural-tour", "sightseeing"], "related_systems": ["narrative-service", "tourism-system", "reputation-system"], "related_documents": [{"id": "canon-region-cis-tbilisi-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/tbilisi/2020-2029/quest-001-old-tbilisi-walk.md", "visibility": "internal", "audience": ["narrative", "quest-design", "localization"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown-версия не раскрывала последовательность прогулки и ключевые точки Старого Тбилиси.", "goal": "Описать маршрут по историческому центру с акцентом на серные бани, крепость Нарикала и панорамы города.", "essence": "Игрок исследует Старый Тбилиси, посещает Абанотубани, крепость Нарикала и статую «Мать-Грузия», завершая прогулку панорамой.", "key_points": ["Серные бани и тёплые источники объясняют происхождение названия города.", "Канатная дорога и крепость Нарикала создают вертикальное приключение.", "Завершение на смотровой площадке добавляет фотозадание и бонус к культуре."]}, "content": {"sections": [{"id": "streets", "title": "Старые улочки", "body": "Игрок начинает маршрут в кварталах с деревянными балконами и мастерскими.\\nAR-гид рассказывает истории ремесленников и архитектурные особенности.\\n", "mechanics_links": [], "assets": []}, {"id": "baths", "title": "Серные бани Абанотубани", "body": "- Визит в банный комплекс с возможностью взять процедурный бафф «Sulfur Relax».\\n- Диалог с банщиком о лечении и традициях.\\n", "mechanics_links": [], "assets": []}, {"id": "narikala", "title": "Подъём к Нарикале", "body": "- Поездка на канатной дороге.\\n- Осмотр крепости IV века и интерактивная реконструкция боёв.\\n", "mechanics_links": [], "assets": []}, {"id": "mother_georgia", "title": "Статуя «Мать-Грузия»", "body": "Игрок изучает символизм меча и чаши, выбирая, какой дар предложить городу: оборону или гостеприимство.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1 000 XP.\\n- Издержки: −30 кредов (посещение бань).\\n- +10 к параметру «Культура».\\n- Ачивка «Хранитель Тбилиси».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Абанотубани", "definition": "Исторический район серных бань Тбилиси, основанный на тёплых источниках."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Прогулка по Старому Тбилиси» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives 